import React, { useState, useEffect } from 'react';
import { useAuth } from '../context/AuthContext'; // Import the AuthContext hook

const AddProductPage = () => {
  const { auth } = useAuth(); // Access vendor details from the context
  const [brand, setBrand] = useState('');
  const [category, setCategory] = useState('Flower'); // Default category
  const [products, setProducts] = useState([]);
  const [selectedProduct, setSelectedProduct] = useState(null);
  const [variations, setVariations] = useState([]); // Store multiple variations
  const [availableStrains, setAvailableStrains] = useState([]);
  const [filteredStrains, setFilteredStrains] = useState([]); // For autocomplete
  const [dropdownVisible, setDropdownVisible] = useState(false); // Track dropdown visibility
  const [selectedImage, setSelectedImage] = useState('');

  // Amount options based on category
  const amountOptions = {
    Flower: ['2.5g', '3.5g', '7g', 'Half-Oz', '1oz'],
    'Pre-roll': ['1g', '2.5g', '3.5g', '5g'],
    Vape: ['0.3g', '0.5g', '1g', '2g'],
    Edible: ['5mg', '10mg', '50mg', '100mg', '200mg', '300mg'],
    Extract: ['1g'],
    Tincture: ['50mg', '100mg', '125mg', '150mg', '200mg', '300mg', '500mg', '1000mg'],
  };

  // Fetch products by brand and category
  useEffect(() => {
    if (brand) {
      const queryParams = new URLSearchParams({ brand, category }).toString();
      fetch(`/api/products/filters?${queryParams}`)
        .then((response) => response.json())
        .then((data) => setProducts(data))
        .catch((error) => console.error('Error fetching products:', error));
    } else {
      setProducts([]);
    }
  }, [brand, category]);

  // Fetch strains for autocomplete
  useEffect(() => {
    fetch('/api/strains')
      .then((response) => response.json())
      .then((data) => {
        setAvailableStrains(data);
        setFilteredStrains(data); // Initialize filtered strains
      })
      .catch((error) => console.error('Error fetching strains:', error));
  }, []);

  const handleProductSelect = (product) => {
    setSelectedProduct(product);
    setVariations([
      {
        amount: '',
        strain: '',
        price: '',
        salePrice: '',
        stock: '',
        image: product.images?.[0] || '',
      },
    ]); // Reset and initialize the first variation
  };

  const handleAddVariation = () => {
    setVariations([
      ...variations,
      { amount: '', strain: '', price: '', salePrice: '', stock: '', image: '' },
    ]);
  };

  const handleVariationChange = (index, key, value) => {
    const updatedVariations = [...variations];
    updatedVariations[index][key] = value;
    setVariations(updatedVariations);

    // Update filtered strains for autocomplete if strain input is updated
    if (key === 'strain') {
      if (value.trim()) {
        setFilteredStrains(
          availableStrains.filter((strain) =>
            strain.name.toLowerCase().includes(value.toLowerCase())
          )
        );
        setDropdownVisible(true); // Show dropdown when typing
      } else {
        setDropdownVisible(false); // Hide dropdown when input is cleared
      }
    }
  };

  const handleSelectStrain = (index, strain) => {
    const updatedVariations = [...variations];
    updatedVariations[index].strain = `${strain.name} (${strain.classification.substring(0, 3).toUpperCase()})`;
    setVariations(updatedVariations);
    setDropdownVisible(false); // Hide dropdown after selection
  };

  const handleRemoveVariation = (index) => {
    const updatedVariations = variations.filter((_, i) => i !== index);
    setVariations(updatedVariations);
  };

  const handleAddToInventory = async () => {
    if (!selectedProduct || variations.some((v) => !v.amount || !v.strain || !v.price || !v.stock)) {
      alert('Please fill out all required fields for each variation.');
      return;
    }

    try {
      const responses = await Promise.all(
        variations.map((variation) =>
          fetch('/api/vendor/inventory', {
            method: 'POST',
            headers: { 'Content-Type': 'application/json' },
            body: JSON.stringify({
              vendorId: auth.vendorId,
              productId: selectedProduct._id,
              variation: {
                amount: variation.amount,
                strain: variation.strain,
                price: parseFloat(variation.price),
                salePrice: variation.salePrice ? parseFloat(variation.salePrice) : undefined,
                stock: parseInt(variation.stock, 10),
                image: variation.image,
              },
              name: selectedProduct.name,
              category: selectedProduct.category,
              brand: selectedProduct.brand,
              description: selectedProduct.description,
            }),
          })
        )
      );

      alert('Products added successfully!');
      setBrand('');
      setCategory('Flower');
      setSelectedProduct(null);
      setVariations([]);
    } catch (error) {
      console.error('Error adding products:', error);
      alert('An error occurred while adding the products.');
    }
  };

  return (
    <div className="container mt-4">
      <h3>Add Product to Inventory</h3>
      <div className="mb-3">
        <label className="form-label">Category</label>
        <select
          className="form-select"
          value={category}
          onChange={(e) => setCategory(e.target.value)}
        >
          <option value="Flower">Flower</option>
          <option value="Pre-roll">Pre-roll</option>
          <option value="Vape">Vape</option>
          <option value="Edible">Edible</option>
          <option value="Extract">Extract</option>
          <option value="Tincture">Tincture</option>
          <option value="Gear">Gear</option>
        </select>
      </div>

      <div className="mb-3">
        <label className="form-label">Brand</label>
        <input
          type="text"
          className="form-control"
          placeholder="Enter brand name"
          value={brand}
          onChange={(e) => setBrand(e.target.value)}
        />
      </div>

      {products.length > 0 && (
        <div className="mb-3">
          <label className="form-label">Matching Products</label>
          <ul className="list-group">
            {products.map((product) => (
              <li
                key={product._id}
                className={`list-group-item ${selectedProduct?._id === product._id ? 'active' : ''}`}
                onClick={() => handleProductSelect(product)}
                style={{ cursor: 'pointer' }}
              >
                <strong>{product.brand}</strong> - {product.name}
              </li>
            ))}
          </ul>
        </div>
      )}

      {selectedProduct && (
        <>
          <h3>Variations</h3>
          {variations.map((variation, index) => (
            <div key={index} className="variation-section mb-4">
              <h5>Variation {index + 1}</h5>
              {category !== 'Gear' && (
                <div className="mb-3">
                  <label className="form-label">Amount</label>
                  <select
                    className="form-select"
                    value={variation.amount}
                    onChange={(e) => handleVariationChange(index, 'amount', e.target.value)}
                  >
                    <option value="" disabled>
                      Select Amount
                    </option>
                    {amountOptions[category]?.map((option, i) => (
                      <option key={i} value={option}>
                        {option}
                      </option>
                    ))}
                  </select>
                </div>
              )}
              <div className="mb-3 position-relative">
                <label className="form-label">Strain</label>
                <input
                  type="text"
                  className="form-control"
                  value={variation.strain}
                  onChange={(e) => handleVariationChange(index, 'strain', e.target.value)}
                />
                {dropdownVisible && filteredStrains.length > 0 && (
                  <ul className="dropdown-menu show position-absolute" style={{ zIndex: 10 }}>
                    {filteredStrains.map((strain, i) => (
                      <li
                        key={i}
                        className="dropdown-item"
                        onClick={() => handleSelectStrain(index, strain)}
                      >
                        {strain.name} ({strain.classification.substring(0, 3).toUpperCase()})
                      </li>
                    ))}
                  </ul>
                )}
              </div>
              <div className="mb-3">
              <label className="form-label">Select Image</label>
              <div className="d-flex overflow-auto" style={{ maxWidth: '100%', gap: '10px' }}>
                {selectedProduct.images.map((image, index) => (
                  <img
                    key={index}
                    src={image}
                    alt={`Product Image ${index + 1}`}
                    onClick={() => handleImageSelect(image)}
                    className={`img-thumbnail ${selectedImage === image ? 'border-primary' : ''}`}
                    style={{
                      cursor: 'pointer',
                      width: '80px',
                      height: '80px',
                      objectFit: 'cover',
                      borderWidth: selectedImage === image ? '3px' : '1px',
                    }}
                  />
                ))}
              </div>
            </div>
              <div className="mb-3">
                <label className="form-label">Price</label>
                <input
                  type="number"
                  className="form-control"
                  value={variation.price}
                  onChange={(e) => handleVariationChange(index, 'price', e.target.value)}
                />
              </div>
              <div className="mb-3">
                <label className="form-label">Sale Price (Optional)</label>
                <input
                  type="number"
                  className="form-control"
                  value={variation.salePrice}
                  onChange={(e) => handleVariationChange(index, 'salePrice', e.target.value)}
                />
              </div>
              <div className="mb-3">
                <label className="form-label">Stock</label>
                <input
                  type="number"
                  className="form-control"
                  value={variation.stock}
                  onChange={(e) => handleVariationChange(index, 'stock', e.target.value)}
                />
              </div>
              <button className="btn btn-danger" onClick={() => handleRemoveVariation(index)}>
                Remove Variation
              </button>
            </div>
          ))}
          <button className="btn btn-secondary mb-3" onClick={handleAddVariation}>
            + Add Variation
          </button>
          <button className="btn btn-success" onClick={handleAddToInventory}>
            Add to Inventory
          </button>
        </>
      )}
    </div>
  );
};

export default AddProductPage;
