import React, { useState, useEffect } from 'react';
import { Form, Button, Row, Col } from 'react-bootstrap';

const BusinessHoursComponent = ({ initialHours = {}, onUpdate }) => {
  const [businessHours, setBusinessHours] = useState(initialHours);

  useEffect(() => {
    setBusinessHours(initialHours);
  }, [initialHours]);

  // Helper function to generate time slots from 6:00 AM to 11:30 PM
  const generateTimeSlots = () => {
    const slots = [];
    for (let hour = 6; hour < 24; hour++) { // Start at 6 AM and end before midnight
      const formattedHour = String(hour).padStart(2, '0');
      slots.push(`${formattedHour}:00`);
      slots.push(`${formattedHour}:30`);
    }
    return slots;
  };

  const availableTimes = generateTimeSlots();

  const handleTimeChange = (day, field, value) => {
    setBusinessHours((prev) => ({
      ...prev,
      [day]: { ...prev[day], [field]: value },
    }));
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    onUpdate(businessHours);
  };

  return (
    <Form onSubmit={handleSubmit} className="p-4 border rounded shadow-sm">
      <h5 className="mb-4">Business Hours</h5>
      {['monday', 'tuesday', 'wednesday', 'thursday', 'friday', 'saturday', 'sunday'].map((day) => (
        <Row key={day} className="align-items-center mb-3">
          <Col xs={12} md={2} className="text-capitalize fw-bold">
            {day}
          </Col>
          <Col xs={5} md={4}>
            <Form.Select
              value={businessHours[day]?.open || ''}
              onChange={(e) => handleTimeChange(day, 'open', e.target.value)}
            >
              <option value="">Open</option>
              {availableTimes.map((time) => (
                <option key={time} value={time}>
                  {time}
                </option>
              ))}
            </Form.Select>
          </Col>
          <Col xs={2} md={1} className="text-center fw-bold">to</Col>
          <Col xs={5} md={4}>
            <Form.Select
              value={businessHours[day]?.close || ''}
              onChange={(e) => handleTimeChange(day, 'close', e.target.value)}
            >
              <option value="">Close</option>
              {availableTimes.map((time) => (
                <option key={time} value={time}>
                  {time}
                </option>
              ))}
            </Form.Select>
          </Col>
        </Row>
      ))}
      <div className="d-flex justify-content-center">
        <Button variant="primary" type="submit" className="mt-3 px-4 py-2">
          Update Hours
        </Button>
      </div>
    </Form>
  );
};

export default BusinessHoursComponent;
