import React from 'react';
import { Card, Badge, Button } from 'react-bootstrap';

const StoreNoticeComponent = ({ storeNotice = '', onEdit }) => {
  return (
    <div className="d-flex flex-column align-items-center">
      <h5 className="text-center mb-4">Store Notice</h5>
      {storeNotice ? (
        <Card className="shadow-sm p-4" style={{ maxWidth: '500px', borderRadius: '20px', position: 'relative' }}>
          {/* Speaking Bubble Pointer */}
          <div
            style={{
              width: '0',
              height: '0',
              borderLeft: '15px solid transparent',
              borderRight: '15px solid transparent',
              borderTop: '15px solid #f8f9fa', // Matches card background color
              position: 'absolute',
              bottom: '-15px',
              left: '50%',
              transform: 'translateX(-50%)',
            }}
          ></div>
          <p className="mb-3 text-center" style={{ fontSize: '1.2rem', color: '#555' }}>
            "{storeNotice}"
          </p>
          <div className="d-flex justify-content-center">
            <Badge bg="info" className="px-3 py-2">
              Active Notice
            </Badge>
          </div>
        </Card>
      ) : (
        <p className="text-muted text-center">No store notice is currently set.</p>
      )}
      <Button
        variant="primary"
        className="mt-4"
        onClick={onEdit} // Trigger parent function to open edit mode
      >
        Edit Notice
      </Button>
    </div>
  );
};

export default StoreNoticeComponent;
