import React, { useState, useContext } from 'react';
import { Button, Modal } from 'react-bootstrap';
import { CartContext } from '../context/CartContext'; // Import CartContext

const ProductRow = ({ product }) => {
  const [showModal, setShowModal] = useState(false);
  const { addToCart } = useContext(CartContext); // Access addToCart from CartContext

  const handleShowModal = () => setShowModal(true);
  const handleCloseModal = () => setShowModal(false);

  // Check if THC content is available
  const thcContentText = product.thcContent ? `${product.thcContent}% THC` : '';
  const amountText = product.amount ? `${product.amount} ${product.uom}` : '';

  return (
    <>
      {/* Product Row */}
      <div className="d-flex align-items-center p-3 mb-3 bg-light border rounded">
        <img
          src={product.primaryImage || 'https://via.placeholder.com/100'}
          alt={product.name}
          style={{ width: '100px', height: '100px', objectFit: 'cover', marginRight: '20px', cursor: 'pointer' }}
          onClick={handleShowModal} // Open modal on image click
        />
        <div className="flex-grow-1">
          <small className="text-muted">{product.brand}</small>
          <h5 className="font-weight-bold mt-1" style={{ fontSize: '1rem', cursor: 'pointer' }} onClick={handleShowModal}>
            {product.name}
          </h5>
          <p>{[thcContentText, amountText].filter(Boolean).join(' | ')}</p>
        </div>
        <div>
          <Button
            variant="primary"
            onClick={() => addToCart(product)}
            style={{ display: 'flex', alignItems: 'center' }}
          >
            <i className="bi bi-plus"></i>&nbsp;
            {product.salePrice ? (
              <>
                <del>+ ${product.price}</del> <span className="text-danger">+ ${product.salePrice}</span>
              </>
            ) : (
              `$${product.price}`
            )}
          </Button>
        </div>
      </div>

      {/* Product Details Modal */}
      <Modal show={showModal} onHide={handleCloseModal} centered>
        <Modal.Header closeButton>
          <Modal.Title>{product.name}</Modal.Title>
        </Modal.Header>
        <Modal.Body className="text-center">
          <img
            src={product.primaryImage || 'https://via.placeholder.com/250'}
            alt={product.name}
            style={{ width: '100%', height: 'auto', marginBottom: '15px' }}
          />
          <p className="text-muted mb-1">{product.brand}</p>
          <p><strong>Classification:</strong> {product.classification}</p>
          <p><strong>Description:</strong> {product.description || 'No description available.'}</p>
        </Modal.Body>
        <Modal.Footer>
          <Button variant="primary" onClick={() => addToCart(product)}>
            <i className="bi bi-plus"></i> Add to Cart - ${product.salePrice || product.price}
          </Button>
          <Button variant="secondary" onClick={handleCloseModal}>Close</Button>
        </Modal.Footer>
      </Modal>
    </>
  );
};

export default ProductRow;
