import React, { useState, useEffect, useContext } from 'react';
import { ListGroup, Badge, Offcanvas, Button } from 'react-bootstrap';
import axios from 'axios';
import { AuthContext } from '../context/AuthContext';

const RecentOrders = () => {
  const { auth } = useContext(AuthContext);
  const [orders, setOrders] = useState([]);
  const [selectedOrder, setSelectedOrder] = useState(null);
  const [showSlideIn, setShowSlideIn] = useState(false);

  useEffect(() => {
    const fetchOrders = async () => {
      if (!auth || !auth.vendorId) {
        console.error('Vendor ID is not defined in AuthContext');
        return;
      }

      try {
        const response = await axios.get(`/api/orders/vendor/${auth.vendorId}`, {
          withCredentials: true,
        });
        console.log('Fetched orders:', response.data); // Debug log
        setOrders(response.data);
      } catch (error) {
        console.error('Error fetching vendor orders:', error);
      }
    };

    fetchOrders();
  }, [auth]);

  const handleRowClick = (order) => {
    console.log('Selected order details:', order); // Debug log
    setSelectedOrder(order);
    setShowSlideIn(true);
  };

  const handleStatusChange = async (status) => {
    if (selectedOrder) {
      try {
        await axios.put(`/api/orders/${selectedOrder._id}/status`, 
          { status }, 
          { headers: { Authorization: `Bearer ${auth.token}` }}
        );
        setOrders((prevOrders) =>
          prevOrders.map((order) =>
            order._id === selectedOrder._id ? { ...order, status } : order
          )
        );
        setShowSlideIn(false);
      } catch (error) {
        console.error('Error updating order status:', error);
      }
    }
  };

  const formatBirthdate = (date) => {
    return new Date(date).toLocaleDateString('en-US', {
      year: 'numeric',
      month: 'long',
      day: 'numeric'
    });
  };

  return (
    <div className="bg-white p-4">
      <h2 className="mb-4">Recent Orders</h2>
      <ListGroup>
        {orders.map((order) => (
          <ListGroup.Item
            key={order._id}
            className="d-flex justify-content-between align-items-center hover:bg-gray-50 cursor-pointer p-4"
            onClick={() => handleRowClick(order)}
          >
            <div className="flex-grow-1">
              <div className="text-gray-500 text-sm">
                {new Date(order.createdAt).toLocaleDateString('en-US', {
                  month: 'short',
                  day: 'numeric',
                  hour: '2-digit',
                  minute: '2-digit'
                })}
              </div>
              <div className="font-bold text-lg">
                {order.contactInfo?.firstName} {order.contactInfo?.lastName}
              </div>
              <div className="flex gap-2 mt-1">
                <Badge bg={
                  order.status === 'COMPLETED' ? 'success' :
                  order.status === 'CANCELLED' ? 'danger' : 'warning'
                }>
                  {order.status.replace('_', ' ')}
                </Badge>

              </div>
            </div>
            <div className="text-right">
            <Badge className="mb-2" bg="info">{order.orderType}</Badge>
              <div className="font-bold text-xl">
                ${order.total?.toFixed(2)}
              </div>
              <div className="text-gray-500">
                {order.items.length} items
              </div>
            </div>
          </ListGroup.Item>
        ))}
      </ListGroup>

      {selectedOrder && (
        <Offcanvas 
          show={showSlideIn} 
          onHide={() => setShowSlideIn(false)} 
          placement="end"
          className="w-96"
        >
          <Offcanvas.Header closeButton className="border-b">
          <Badge className="me-2" bg="info">{selectedOrder.orderType}</Badge>
            <Offcanvas.Title>Order Details</Offcanvas.Title>
          </Offcanvas.Header>
          <Offcanvas.Body>
            <div className="space-y-6">
              {/* Order Info */}
              <div>
                <div className="text-gray-500 text-sm">
                  {new Date(selectedOrder.createdAt).toLocaleString()}
                </div>
                <div className="font-bold text-xl mt-1">
                  Order #{selectedOrder._id.slice(-6)}
                </div>
              </div>

              {/* Customer Info */}
              <div>
                <h5 className="font-semibold mb-2">Customer Information</h5>
                <div className="space-y-1">
                  <p className="text-gray-800">
                    {selectedOrder.contactInfo?.firstName} {selectedOrder.contactInfo?.lastName}
                  </p>
                  <p className="text-gray-600">
                    DOB: {formatBirthdate(selectedOrder.customer?.birthdate)}
                  </p>
                  <p className="text-gray-600">
                    {selectedOrder.contactInfo?.email}
                  </p>
                  <p className="text-gray-600">
                    {selectedOrder.contactInfo?.phone}
                  </p>
                </div>
              </div>

              {/* Delivery Info (if applicable) */}
              {selectedOrder.orderType === 'Delivery' && selectedOrder.deliveryAddress && (
                <div>
                  <h5 className="font-semibold mb-2">Delivery Address</h5>
                  <div className="text-gray-800">
                    {selectedOrder.deliveryAddress.street}<br />
                    {selectedOrder.deliveryAddress.city}, {selectedOrder.deliveryAddress.state} {selectedOrder.deliveryAddress.zip}
                  </div>
                </div>
              )}

              {/* Order Items */}
              <div>
                <h5 className="font-semibold mb-2">Items</h5>
                <ListGroup>
                  {selectedOrder.items.map((item, index) => (
                    <ListGroup.Item key={index} className="border-0 px-0">
                      <div className="flex items-center gap-3">
                        {item.image && (
                          <img 
                            src={item.image} 
                            alt={item.name}
                            className="w-16 h-16 object-cover rounded"
                          />
                        )}
                        <div className="flex-grow">
                          <div className="font-medium">{item.name}</div>
                          <div className="text-gray-500">
                            {item.salePrice ? (
                              <span>
                                <span className="line-through mr-2">${item.price?.toFixed(2)}</span>
                                <span className="text-red-600">${item.salePrice?.toFixed(2)}</span>
                              </span>
                            ) : (
                              <span>${item.price?.toFixed(2)}</span>
                            )} x {item.quantity}
                          </div>
                          {item.category && (
                            <div className="text-sm text-gray-500">
                              {item.category}
                            </div>
                          )}
                        </div>
                        <div className="font-medium">
                          ${((item.salePrice || item.price) * item.quantity).toFixed(2)}
                        </div>
                      </div>
                    </ListGroup.Item>
                  ))}
                </ListGroup>
                <div className="mt-3 p-3 bg-gray-50 rounded space-y-2">
                  <div className="flex justify-between text-sm">
                    <span>Subtotal</span>
                    <span>${selectedOrder.items.reduce((sum, item) => 
                      sum + ((item.salePrice || item.price) * item.quantity), 0).toFixed(2)}</span>
                  </div>
                  <div className="flex justify-between font-bold">
                    <span>Total</span>
                    <span>${selectedOrder.total?.toFixed(2)}</span>
                  </div>
                </div>
              </div>

              {/* Action Buttons */}
              <div className="flex justify-between gap-4 mt-6">
                <Button
                  variant="danger"
                  className="flex-1"
                  onClick={() => handleStatusChange('CANCELLED')}
                >
                  Cancel Order
                </Button>
                <Button
                  variant="success"
                  className="flex-1"
                  onClick={() => handleStatusChange('COMPLETED')}
                >
                  Mark as Completed
                </Button>
              </div>
            </div>
          </Offcanvas.Body>
        </Offcanvas>
      )}
    </div>
  );
};

export default RecentOrders;
