import React, { useContext } from 'react';
import { Button, Row, Col } from 'react-bootstrap';
import { CartContext } from '../context/CartContext'; // Assuming you have a CartContext to manage the cart

const PlaceOrder = () => {
  const { calculateTotal, cartItems } = useContext(CartContext); // Get cart data from context

  const subtotal = calculateTotal();
  const taxRate = 0.17;
  const estimatedTotal = subtotal + subtotal * taxRate;

  // Function to handle order placement
  const handlePlaceOrder = async () => {
    const orderDetails = {
      items: cartItems, // Items in the cart
      subtotal: subtotal.toFixed(2),
      total: estimatedTotal.toFixed(2),
      orderDate: new Date().toISOString(),
    };

    // Notify vendors and store the order in the database
    try {
      // Make API request to notify vendors and store order
      const response = await fetch('/api/orders', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(orderDetails),
      });

      if (response.ok) {
        alert('Order placed successfully! Vendors have been notified.');
      } else {
        alert('Failed to place order. Please try again.');
      }
    } catch (error) {
      console.error('Error placing order:', error);
      alert('An error occurred. Please try again later.');
    }
  };

  return (
    <div className="place-order-section p-4 bg-light" style={{
        backgroundColor: '#f8f9fa', // Light background
        margin: '20px', // Small margin from screen edges
        borderRadius: '8px',
      }}>
      <Row className="mb-3">
        <Col>
          <strong>Subtotal:</strong>
        </Col>
        <Col className="text-end">${subtotal.toFixed(2)}</Col>
      </Row>
      <Row className="mb-3">
        <Col>
          <strong>Estimated Total w/ Tax:</strong>
        </Col>
        <Col className="text-end">${estimatedTotal.toFixed(2)}</Col>
      </Row>
      <Button
        variant="primary"
        className="w-100"
        onClick={handlePlaceOrder} // Trigger place order function
      >
        Place Order
      </Button>
    </div>
  );
};

export default PlaceOrder;
