import React, { useState, useEffect } from 'react';
import { Card, Button, Modal, Form, Alert } from 'react-bootstrap';
import axios from 'axios';

const VendorInfoCard = () => {
    const [vendor, setVendor] = useState({});
    const [showModal, setShowModal] = useState(false);
    const [formData, setFormData] = useState({
        firstName: '',
        lastName: '',
        contactNumber: {
            formatted: '',
            e164: ''
        },
        email: '',
    });
    const [alert, setAlert] = useState({ message: '', variant: '' });

    // Fetch vendor details on component mount
    useEffect(() => {
        const fetchVendorData = async () => {
            try {
                const response = await axios.get('/api/vendors/profile', {
                    headers: {
                        Authorization: `Bearer ${localStorage.getItem('token')}`,
                    },
                });
                setVendor(response.data.data);
            } catch (error) {
                console.error('Error fetching vendor details:', error);
                setAlert({ message: 'Failed to load vendor details.', variant: 'danger' });
            }
        };
        fetchVendorData();
    }, []);

    // Open modal and prefill form with current details
    const handleEdit = () => {
        const currentPhone = vendor.contactNumber || {};
        setFormData({
            firstName: vendor.firstName || '',
            lastName: vendor.lastName || '',
            contactNumber: {
                formatted: formatPhoneNumber(typeof currentPhone === 'object' ? 
                    currentPhone.formatted || '' : currentPhone || ''),
                e164: typeof currentPhone === 'object' ? 
                    currentPhone.e164 || '' : formatE164(currentPhone || '')
            },
            email: vendor.email || '',
        });
        setShowModal(true);
    };

    // Format phone number for display as (###) ###-####
    const formatPhoneNumber = (value) => {
        const cleaned = value.replace(/\D/g, '');
        const match = cleaned.match(/^(\d{0,3})(\d{0,3})(\d{0,4})$/);
        if (!match) return value;
        return [
            match[1] && `(${match[1]}`,
            match[2] && `${match[1] ? ') ' : ''}${match[2]}`,
            match[3] && `-${match[3]}`,
        ]
            .filter(Boolean)
            .join('');
    };

    // Format phone number as E.164
    const formatE164 = (value) => {
        const cleaned = value.replace(/\D/g, '');
        return cleaned.length === 10 ? `+1${cleaned}` : '';
    };

    // Validate email
    const validateEmail = (email) => {
        const emailRegex = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
        return emailRegex.test(email);
    };

    // Handle form input changes
    const handleChange = (e) => {
        const { name, value } = e.target;
        if (name === 'contactNumber') {
            const cleaned = value.replace(/\D/g, '');
            setFormData(prev => ({
                ...prev,
                contactNumber: {
                    formatted: formatPhoneNumber(value),
                    e164: formatE164(cleaned)
                }
            }));
        } else {
            setFormData(prev => ({ ...prev, [name]: value }));
        }
    };

    // Handle form submission
    const handleUpdate = async () => {
        // Validate phone number
        const phoneNumber = formData.contactNumber.formatted.replace(/\D/g, '');
        if (phoneNumber.length !== 10) {
            setAlert({ message: 'Phone number must be 10 digits.', variant: 'danger' });
            return;
        }

        // Validate email
        if (!validateEmail(formData.email)) {
            setAlert({ message: 'Invalid email address.', variant: 'danger' });
            return;
        }

        try {
            const response = await axios.put('/api/vendors/profile', {
                ...formData,
                contactNumber: {
                    formatted: formData.contactNumber.formatted,
                    e164: formData.contactNumber.e164
                }
            }, {
                headers: {
                    Authorization: `Bearer ${localStorage.getItem('token')}`,
                },
            });
            setVendor(response.data.data);
            setAlert({ message: 'Details updated successfully!', variant: 'success' });
            setShowModal(false);
        } catch (error) {
            console.error('Error updating vendor details:', error);
            setAlert({ message: 'Failed to update details.', variant: 'danger' });
        }
    };

    // Display formatted phone number from either format
    const displayPhoneNumber = (contactNumber) => {
        if (!contactNumber) return 'N/A';
        if (typeof contactNumber === 'object') {
            return contactNumber.formatted || 'N/A';
        }
        return formatPhoneNumber(contactNumber);
    };

    return (
        <div>
            {alert.message && <Alert variant={alert.variant}>{alert.message}</Alert>}

            <Card className="shadow-sm m-0" style={{ width: '300px', margin: '0 auto' }}>
                <Card.Body>
                    <div className="d-flex justify-content-between align-items-center">
                        <div className="me-3">
                            <Card.Title>
                                {vendor.firstName || 'N/A'} {vendor.lastName || 'N/A'}
                            </Card.Title>
                            <Card.Text className="mb-0">
                                {displayPhoneNumber(vendor.contactNumber)} <br />
                                {vendor.email || 'N/A'}
                            </Card.Text>
                        </div>
                        <div>
                            <Button 
                                variant="outline-secondary" 
                                size="sm" 
                                onClick={handleEdit}
                            >
                                Edit
                            </Button>
                        </div>
                    </div>
                </Card.Body>
            </Card>

            <Modal show={showModal} onHide={() => setShowModal(false)} centered>
                <Modal.Header closeButton>
                    <Modal.Title>Edit Vendor Information</Modal.Title>
                </Modal.Header>
                <Modal.Body>
                    <Form>
                        <Form.Group className="mb-3">
                            <Form.Label>First Name</Form.Label>
                            <Form.Control
                                type="text"
                                name="firstName"
                                value={formData.firstName}
                                onChange={handleChange}
                            />
                        </Form.Group>
                        <Form.Group className="mb-3">
                            <Form.Label>Last Name</Form.Label>
                            <Form.Control
                                type="text"
                                name="lastName"
                                value={formData.lastName}
                                onChange={handleChange}
                            />
                        </Form.Group>
                        <Form.Group className="mb-3">
                            <Form.Label>Contact Number</Form.Label>
                            <Form.Control
                                type="text"
                                name="contactNumber"
                                value={formData.contactNumber.formatted}
                                onChange={handleChange}
                                maxLength="14"
                            />
                        </Form.Group>
                        <Form.Group className="mb-3">
                            <Form.Label>Email</Form.Label>
                            <Form.Control
                                type="email"
                                name="email"
                                value={formData.email}
                                onChange={handleChange}
                            />
                        </Form.Group>
                    </Form>
                </Modal.Body>
                <Modal.Footer>
                    <Button variant="secondary" onClick={() => setShowModal(false)}>
                        Cancel
                    </Button>
                    <Button variant="primary" onClick={handleUpdate}>
                        Update
                    </Button>
                </Modal.Footer>
            </Modal>
        </div>
    );
};

export default VendorInfoCard;
