import React, { useState, useEffect } from 'react';
import { Navbar, Image } from 'react-bootstrap';
import { Link, useNavigate } from 'react-router-dom';
import axios from 'axios';
import { GeoAltFill } from 'react-bootstrap-icons';

const CustomNavbar = () => {
  const [parsedAddress, setParsedAddress] = useState({ street: '', city: '', stateAbbreviation: '' }); // User's address
  const navigate = useNavigate(); // For navigation

  useEffect(() => {
    // Parse user's saved address
    const savedAddress = localStorage.getItem('userAddress');
    if (savedAddress) {
      const parts = savedAddress.split(',');
      setParsedAddress({
        street: parts[0]?.trim() || '',
        city: parts[1]?.trim() || '',
        stateAbbreviation: parts[2]?.trim().split(' ')[0] || '',
      });
    }
  }, []);

  // Function to clear address and navigate home
  const clearAddressAndNavigate = () => {
    localStorage.removeItem('userAddress');
    navigate('/');
  };

  return (
    <Navbar style={{ backgroundColor: '#FFFAFA' }} expand="lg" className="px-3 navbar">
      {/* Left Side: Logo */}
      <Navbar.Brand as={Link} to="/" className="d-flex align-items-center">
        <Image
          src={require('../assets/images/addressPageLogo.png')}
          alt="logo"
          height="32"
          className="d-inline-block align-top rounded me-2"
        />
      </Navbar.Brand>

      {/* Right Side: GeoAlt Icon and Address */}
      <div className="ms-auto d-flex align-items-center" style={{ cursor: 'pointer' }} onClick={clearAddressAndNavigate}>
        <GeoAltFill className="me-2" size={17} style={{ color: '#333333' }} />
        {parsedAddress.street || parsedAddress.city || parsedAddress.stateAbbreviation ? (
          <span style={{ fontWeight: '600', fontFamily: 'Poppins, sans-serif', color: '#333333' }}>
            {parsedAddress.street || `${parsedAddress.city}, ${parsedAddress.stateAbbreviation}`}
          </span>
        ) : null}
      </div>
    </Navbar>
  );
};

export default CustomNavbar;
