import React, { createContext, useState, useEffect, useContext } from 'react';
import axios from 'axios';

export const AuthContext = createContext();

export const AuthProvider = ({ children }) => {
    const [auth, setAuth] = useState(null); // Store auth object or null
    const [loading, setLoading] = useState(true); // Loading state for initialization

    useEffect(() => {
        const initializeAuth = async () => {
            const storedAuth = JSON.parse(localStorage.getItem('auth'));
            if (storedAuth?.refreshToken) {
                try {
                    // Attempt to refresh token using the stored refresh token
                    const response = await axios.post('/api/auth/refresh-token', {}, {
                        headers: { Authorization: `Bearer ${storedAuth.refreshToken}` },
                    });

                    const updatedAuth = {
                        ...storedAuth,
                        token: response.data.accessToken,
                        refreshToken: response.data.refreshToken,
                    };

                    setAuth(updatedAuth); // Set auth state
                    localStorage.setItem('auth', JSON.stringify(updatedAuth)); // Update localStorage
                } catch (error) {
                    console.error('Failed to refresh token:', error);
                    setAuth(null);
                    localStorage.removeItem('auth');
                }
            }
            setLoading(false); // Auth initialization complete
        };
        initializeAuth();
    }, []);

    const login = (authData) => {
        localStorage.setItem('auth', JSON.stringify(authData));
        setAuth(authData);
    };

    const logout = () => {
        localStorage.removeItem('auth');
        setAuth(null);
    };

    return (
        <AuthContext.Provider value={{ auth, login, logout, loading }}>
            {loading ? <div>Loading...</div> : children}
        </AuthContext.Provider>
    );
};

export const useAuth = () => useContext(AuthContext);
