import React, { useState, useEffect } from 'react';
import { Card, Button, Modal, Form, Alert } from 'react-bootstrap';
import axios from 'axios';

const VendorInfoCard = () => {
    const [vendor, setVendor] = useState({});
    const [showModal, setShowModal] = useState(false);
    const [formData, setFormData] = useState({
        firstName: '',
        lastName: '',
        contactNumber: '',
        email: '',
    });
    const [alert, setAlert] = useState({ message: '', variant: '' });

    // Fetch vendor details on component mount
    useEffect(() => {
        const fetchVendorData = async () => {
            try {
                const response = await axios.get('/api/vendors/profile', {
                    headers: {
                        Authorization: `Bearer ${localStorage.getItem('token')}`,
                    },
                });
                setVendor(response.data.data);
            } catch (error) {
                console.error('Error fetching vendor details:', error);
                setAlert({ message: 'Failed to load vendor details.', variant: 'danger' });
            }
        };
        fetchVendorData();
    }, []);

    // Open modal and prefill form with current details
    const handleEdit = () => {
        setFormData({
            firstName: vendor.firstName || '',
            lastName: vendor.lastName || '',
            contactNumber: vendor.contactNumber || '',
            email: vendor.email || '',
        });
        setShowModal(true);
    };

    // Handle form input changes
    const handleChange = (e) => {
        setFormData({ ...formData, [e.target.name]: e.target.value });
    };

    // Handle form submission
    const handleUpdate = async () => {
        try {
            const response = await axios.put('/api/vendors/profile', formData, {
                headers: {
                    Authorization: `Bearer ${localStorage.getItem('token')}`,
                },
            });
            setVendor(response.data.data); // Update vendor details in state
            setAlert({ message: 'Details updated successfully!', variant: 'success' });
            setShowModal(false); // Close modal
        } catch (error) {
            console.error('Error updating vendor details:', error);
            setAlert({ message: 'Failed to update details.', variant: 'danger' });
        }
    };

    return (
        <div>
            {/* Display alert if available */}
            {alert.message && <Alert variant={alert.variant}>{alert.message}</Alert>}

            {/* Vendor Details Card */}
            <Card className="shadow-sm mt-3" style={{ width: '24rem', margin: '0 auto' }}>
                <Card.Body>
                    <Card.Title>Vendor Information</Card.Title>
                    <Card.Text>
                        <strong>First Name:</strong> {vendor.firstName || 'N/A'} <br />
                        <strong>Last Name:</strong> {vendor.lastName || 'N/A'} <br />
                        <strong>Contact Number:</strong> {vendor.contactNumber || 'N/A'} <br />
                        <strong>Email:</strong> {vendor.email || 'N/A'}
                    </Card.Text>
                    <Button variant="primary" onClick={handleEdit}>
                        Edit
                    </Button>
                </Card.Body>
            </Card>

            {/* Edit Modal */}
            <Modal show={showModal} onHide={() => setShowModal(false)} centered>
                <Modal.Header closeButton>
                    <Modal.Title>Edit Vendor Information</Modal.Title>
                </Modal.Header>
                <Modal.Body>
                    <Form>
                        <Form.Group className="mb-3">
                            <Form.Label>First Name</Form.Label>
                            <Form.Control
                                type="text"
                                name="firstName"
                                value={formData.firstName}
                                onChange={handleChange}
                            />
                        </Form.Group>
                        <Form.Group className="mb-3">
                            <Form.Label>Last Name</Form.Label>
                            <Form.Control
                                type="text"
                                name="lastName"
                                value={formData.lastName}
                                onChange={handleChange}
                            />
                        </Form.Group>
                        <Form.Group className="mb-3">
                            <Form.Label>Contact Number</Form.Label>
                            <Form.Control
                                type="text"
                                name="contactNumber"
                                value={formData.contactNumber}
                                onChange={handleChange}
                            />
                        </Form.Group>
                        <Form.Group className="mb-3">
                            <Form.Label>Email</Form.Label>
                            <Form.Control
                                type="email"
                                name="email"
                                value={formData.email}
                                onChange={handleChange}
                            />
                        </Form.Group>
                    </Form>
                </Modal.Body>
                <Modal.Footer>
                    <Button variant="secondary" onClick={() => setShowModal(false)}>
                        Cancel
                    </Button>
                    <Button variant="primary" onClick={handleUpdate}>
                        Update
                    </Button>
                </Modal.Footer>
            </Modal>
        </div>
    );
};

export default VendorInfoCard;
