import React from 'react';
import { Container, Row, Col } from 'react-bootstrap';
import ProductCard from './ProductCard';

const MenuDisplay = ({ products }) => {
  if (!products || products.length === 0) {
    return <p>No products available.</p>; // Handle empty or undefined products
  }

  // Step 1: Sort products so products with salePrice appear first
  const sortedProducts = [...products].sort((a, b) => {
    const aHasSalePrice = a?.variation?.salePrice != null;
    const bHasSalePrice = b?.variation?.salePrice != null;

    // Products with salePrice come first
    if (aHasSalePrice && !bHasSalePrice) return -1;
    if (!aHasSalePrice && bHasSalePrice) return 1;
    return 0; // Maintain order otherwise
  });

  // Step 2: Group sorted products by category
  const productsByCategory = sortedProducts.reduce((categories, product) => {
    const category = product.category || 'Uncategorized'; // Default to 'Uncategorized' if no category
    if (!categories[category]) {
      categories[category] = [];
    }
    categories[category].push(product);
    return categories;
  }, {});

  return (
    <div
      style={{
        backgroundColor: '#FDFDFD',
        padding: '2rem 1rem',
        borderRadius: '8px',
        position: 'relative',
        overflow: 'hidden',
      }}
    >
      <Container fluid style={{ paddingLeft: '15px', paddingRight: '15px' }}>
        {Object.keys(productsByCategory).map((category) => (
          <div key={category} style={{ marginBottom: '2rem' }}>
            {/* Category Heading */}
            <h5
              style={{
                fontWeight: 'bold',
                marginBottom: '1rem',
                textTransform: 'uppercase',
                borderBottom: '2px solid #ddd',
                paddingBottom: '0.5rem',
              }}
            >
              {category}
            </h5>
            {/* Products Row */}
            <Row>
              {productsByCategory[category].map((product) => (
                <Col
                  key={product._id}
                  xs={12} // Full-width on extra-small screens
                  sm={6} // Two columns on small screens
                  md={4} // Three columns on medium screens
                  lg={3} // Four columns on large screens
                  xl={2} // Six columns on extra-large screens
                  className="mb-3"
                >
                  <ProductCard product={product} />
                </Col>
              ))}
            </Row>
          </div>
        ))}
      </Container>
    </div>
  );
};

export default MenuDisplay;
