import React, { useState, useEffect } from 'react';
import { useAuth } from '../context/AuthContext'; // Import the AuthContext hook

const AddProductPage = () => {
  const { auth } = useAuth(); // Access vendor details from the context
  const [brand, setBrand] = useState('');
  const [category, setCategory] = useState('Flower'); // Default category
  const [products, setProducts] = useState([]);
  const [selectedProduct, setSelectedProduct] = useState(null);
  const [price, setPrice] = useState('');
  const [salePrice, setSalePrice] = useState('');
  const [stock, setStock] = useState('');
  const [selectedImage, setSelectedImage] = useState('');
  const [variation, setVariation] = useState(null);
  const [weight, setWeight] = useState('');
  const [thcContent, setThcContent] = useState('');

  // Fetch products by brand and category
  useEffect(() => {
    if (brand) {
      const queryParams = new URLSearchParams({ brand, category }).toString();
      fetch(`/api/products/filters?${queryParams}`)
        .then((response) => response.json())
        .then((data) => setProducts(data))
        .catch((error) => console.error('Error fetching products:', error));
    } else {
      setProducts([]);
    }
  }, [brand, category]);

  const handleAddToInventory = async () => {
    if (!selectedProduct || !price || !stock) {
      alert('Please fill out all required fields.');
      return;
    }

    try {
      const response = await fetch('/api/vendor/inventory', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({
          vendorId: auth.vendorId,
          productId: selectedProduct._id,
          price: parseFloat(price),
          stock: parseInt(stock, 10),
          weight,
          variation,
          thcContent: thcContent ? parseFloat(thcContent) : undefined,
          salePrice: salePrice ? parseFloat(salePrice) : undefined,
          selectedImage,
        }),
      });

      const data = await response.json();

      if (response.ok) {
        alert(data.message);
        // Reset form
        setBrand('');
        setCategory('Flower');
        setSelectedProduct(null);
        setPrice('');
        setStock('');
        setSalePrice('');
        setWeight('');
        setThcContent('');
        setSelectedImage('');
        setVariation(null);
      } else {
        alert(data.message || 'Failed to add product to inventory.');
      }
    } catch (error) {
      console.error('Error adding product to inventory:', error);
      alert('An error occurred while adding the product.');
    }
  };

  const handleProductSelect = (product) => {
    setSelectedProduct(product);
    setSelectedImage(product.images?.[0] || '');
    setVariation(null);
  };

  return (
    <div className="container mt-4">
      <h1>Add Product to Inventory</h1>
      {/* Product Selection */}
      <div className="mb-3">
        <label className="form-label">Category</label>
        <select
          className="form-select"
          value={category}
          onChange={(e) => setCategory(e.target.value)}
        >
          <option value="Flower">Flower</option>
          <option value="Pre-roll">Pre-roll</option>
          <option value="Vape">Vape</option>
          <option value="Edible">Edible</option>
          <option value="Extract">Extract</option>
          <option value="Topical">Topical</option>
          <option value="Gear">Gear</option>
        </select>
      </div>
      <div className="mb-3">
        <label className="form-label">Brand</label>
        <input
          type="text"
          className="form-control"
          placeholder="Enter brand name"
          value={brand}
          onChange={(e) => setBrand(e.target.value)}
        />
      </div>
      {products.length > 0 && (
        <div className="mb-3">
          <label className="form-label">Matching Products</label>
          <ul className="list-group">
            {products.map((product) => (
              <li
                key={product._id}
                className={`list-group-item ${selectedProduct?._id === product._id ? 'active' : ''}`}
                onClick={() => handleProductSelect(product)}
                style={{ cursor: 'pointer' }}
              >
                <strong>{product.brand}</strong> - {product.name}{' '}
                {product.images?.[0] && (
                  <img
                    src={product.images[0]}
                    alt={product.name}
                    style={{ width: '40px', marginLeft: '10px' }}
                  />
                )}
              </li>
            ))}
          </ul>
        </div>
      )}

      {selectedProduct && (
        <>
          {/* Image Selection */}
          {selectedProduct.images?.length > 1 && (
            <div className="mb-3">
              <label className="form-label">Select Image</label>
              <div className="d-flex overflow-auto">
                {selectedProduct.images.map((img, index) => (
                  <img
                    key={index}
                    src={img}
                    alt={`Product Image ${index}`}
                    className={`img-thumbnail me-2 ${selectedImage === img ? 'border-primary' : ''}`}
                    style={{ cursor: 'pointer', width: '60px', height: '60px' }}
                    onClick={() => setSelectedImage(img)}
                  />
                ))}
              </div>
            </div>
          )}

          {/* Variation Selection */}
          {selectedProduct.variations?.length > 0 && (
            <div className="mb-3">
              <label className="form-label">Select Variation</label>
              <select
                className="form-select"
                value={variation || ''}
                onChange={(e) => setVariation(e.target.value)}
              >
                <option value="" disabled>
                  Select a strain
                </option>
                {selectedProduct.variations.map((v, index) => (
                  <option key={index} value={v.strain}>
                    {v.strain} ({v.classification})
                  </option>
                ))}
              </select>
            </div>
          )}

          {/* Weight Input */}
          <div className="mb-3">
            <label className="form-label">Weight</label>
            <input
              type="text"
              className="form-control"
              placeholder="Enter weight (e.g., 3.5g)"
              value={weight}
              onChange={(e) => setWeight(e.target.value)}
            />
          </div>

          {/* THC Content */}
          <div className="mb-3">
            <label className="form-label">THC Content (%)</label>
            <input
              type="number"
              className="form-control"
              placeholder="Optional"
              value={thcContent}
              onChange={(e) => setThcContent(e.target.value)}
            />
          </div>

          {/* Price Inputs */}
          <div className="mb-3">
            <label className="form-label">Price</label>
            <input
              type="number"
              className="form-control"
              placeholder="Enter price"
              value={price}
              onChange={(e) => setPrice(e.target.value)}
            />
          </div>
          <div className="mb-3">
            <label className="form-label">Sale Price (Optional)</label>
            <input
              type="number"
              className="form-control"
              placeholder="Enter sale price"
              value={salePrice}
              onChange={(e) => setSalePrice(e.target.value)}
            />
          </div>

          {/* Stock Input */}
          <div className="mb-3">
            <label className="form-label">Stock</label>
            <input
              type="number"
              className="form-control"
              placeholder="Enter stock"
              value={stock}
              onChange={(e) => setStock(e.target.value)}
            />
          </div>

          <button className="btn btn-success" onClick={handleAddToInventory}>
            Add to Inventory
          </button>
        </>
      )}
    </div>
  );
};

export default AddProductPage;
