import React, { useState, useEffect } from 'react';
import { Offcanvas, ListGroup, Button, Form, InputGroup, FormControl, Badge, Modal } from 'react-bootstrap';
import axios from 'axios';
import { useAuth } from '../context/AuthContext';
import api from '../api/api';

const InventoryComponent = () => {
  const { auth } = useAuth();
  const [inventory, setInventory] = useState({});
  const [filteredInventory, setFilteredInventory] = useState({});
  const [searchTerm, setSearchTerm] = useState('');
  const [selectedProduct, setSelectedProduct] = useState(null);
  const [showSlideIn, setShowSlideIn] = useState(false);
  const [showDeleteModal, setShowDeleteModal] = useState(false);
  const [updatedDetails, setUpdatedDetails] = useState({ 
    price: '', 
    salePrice: '', 
    tags: [] 
  });

  const availableTags = ['Staff Pick', 'Low THC', 'High THC'];

  // Currency formatter
  const formatter = new Intl.NumberFormat('en-US', {
    style: 'currency',
    currency: 'USD',
  });

  const fetchInventory = async () => {
    if (!auth?.vendorId) {
      console.error('Vendor ID is not defined in AuthContext');
      return;
    }

    try {
      const response = await api.get(`/vendor/inventory/${auth.vendorId}`);
      const groupedData = response.data.reduce((acc, item) => {
        const category = item.category || 'Uncategorized';
        if (!acc[category]) acc[category] = [];
        acc[category].push(item);
        return acc;
      }, {});
      setInventory(groupedData);
      setFilteredInventory(groupedData);
    } catch (error) {
      console.error('Error fetching inventory:', error);
    }
  };

  useEffect(() => {
    fetchInventory();

    // Add event listener for inventory updates
    const handleInventoryUpdate = () => {
      fetchInventory();
    };

    window.addEventListener('inventoryUpdated', handleInventoryUpdate);

    // Cleanup
    return () => {
      window.removeEventListener('inventoryUpdated', handleInventoryUpdate);
    };
  }, [auth]);

  const handleRefresh = async () => {
    await fetchInventory();
  };

  const handleSearch = (e) => {
    const term = e.target.value.toLowerCase();
    setSearchTerm(term);

    if (!term) {
      setFilteredInventory(inventory);
      return;
    }

    const newFilteredInventory = {};
    Object.keys(inventory).forEach((category) => {
      const filteredProducts = inventory[category].filter(
        (product) =>
          product.name.toLowerCase().includes(term) ||
          product.brand.toLowerCase().includes(term)
      );
      if (filteredProducts.length) newFilteredInventory[category] = filteredProducts;
    });
    setFilteredInventory(newFilteredInventory);
  };

  const handleRowClick = (product) => {
    setSelectedProduct({
      ...product,
      name: product.name || product.productId?.name,
      description: product.description || product.productId?.description,
      status: product.status || 'Active',
    });
    setUpdatedDetails({
      price: product.variation?.price || '',
      salePrice: product.variation?.salePrice || '',
      tags: product.variation?.tags || []
    });
    setShowSlideIn(true);
  };



  const handleUpdate = async () => {
    const { price, salePrice } = updatedDetails;

    if (salePrice && parseFloat(salePrice) >= parseFloat(price)) {
      alert('Sale price must be less than the regular price.');
      return;
    }

    try {
      const updatedProduct = {
        variation: {
          ...selectedProduct.variation,
          price: parseFloat(updatedDetails.price),
          salePrice: salePrice === '' ? null : salePrice ? parseFloat(salePrice) : null,
          tags: updatedDetails.tags  
        }
      };

      await axios.put(`/api/vendor/inventory/${selectedProduct._id}`, updatedProduct);

      setInventory((prevInventory) => {
        const updated = { ...prevInventory };
        const category = selectedProduct.category;
        if (updated[category]) {
          updated[category] = updated[category].map((item) =>
            item._id === selectedProduct._id
              ? {
                  ...item,
                  variation: {
                    ...item.variation,
                    price: parseFloat(updatedDetails.price),
                    salePrice: updatedDetails.salePrice ? parseFloat(updatedDetails.salePrice) : undefined,
                    tags: updatedDetails.tags
                  }
                }
              : item
          );
        }
        return updated;
      });

      setShowSlideIn(false);
    } catch (error) {
      console.error('Error updating product details:', error);
      alert('Failed to update product details.');
    }
  };

  const handleDelete = async () => {
    try {
      await axios.delete(`/api/vendor/inventory/${selectedProduct._id}`);

      setInventory((prev) => {
        const updated = { ...prev };
        const category = selectedProduct.category;
        if (updated[category]) {
          updated[category] = updated[category].filter((item) => item._id !== selectedProduct._id);
        }
        return updated;
      });

      setShowDeleteModal(false);
      setShowSlideIn(false);
    } catch (error) {
      console.error('Error deleting product:', error);
      alert('Failed to delete product.');
    }
  };

  const handleTagToggle = (tag) => {
    setUpdatedDetails(prev => {
      const currentTags = [...prev.tags];
      const tagIndex = currentTags.indexOf(tag);

      if (tagIndex === -1) {
        currentTags.push(tag);
      } else {
        currentTags.splice(tagIndex, 1);
      }

      return {
        ...prev,
        tags: currentTags
      };
    });
  };

  return (
    <div>
      {/* Search and Refresh Section */}
      <div className="bg-white pb-3 px-4 mb-3">
        <div className="d-flex align-items-center justify-content-between">
          <InputGroup className="w-50">
            <FormControl
              placeholder="Search by brand or product name"
              value={searchTerm}
              onChange={handleSearch}
            />
          </InputGroup>
          <div className="d-flex align-items-center">
            <Button
              variant="outline-primary"
              className="rounded-circle me-3"
              onClick={handleRefresh}
              style={{
                width: '40px',
                height: '40px',
                display: 'flex',
                alignItems: 'center',
                justifyContent: 'center',
              }}
            >
              <i className="bi bi-arrow-clockwise"></i>
            </Button>
          </div>
        </div>
      </div>

      {/* Product List */}
      {Object.keys(filteredInventory).map((category) => (
        <div key={category}>
          <h4 className="mt-4">{category}</h4>
          <ListGroup>
            {filteredInventory[category].map((product) => (
              <ListGroup.Item
                key={product._id}
                className="d-flex justify-content-between align-items-center"
                style={{
                  cursor: 'pointer',
                  opacity: product.status === 'Disabled' ? 0.5 : 1,
                }}
                onClick={() => handleRowClick(product)}
              >
                <div className="d-flex align-items-center">
                  <img
                    src={product.variation?.image}
                    alt={product.name}
                    className="me-3"
                    style={{ width: '50px', height: '50px', objectFit: 'cover' }}
                  />
                  <div>
                    <div className="fw-bold">
                      {product.brand} | <span className="fw-medium">{product.variation?.amount}</span>
                    </div>
                    <div className="fw-medium">
                      {product.name}
                    </div>
                    {product.variation?.strain && (
                      <span className="text-muted me-2" style={{ fontSize: '0.85rem' }}>
                        {product.variation.strain}
                      </span>
                    )}
                  </div>
                </div>
                <div className="d-flex align-items-center">
                  {product.status === 'Disabled' && (
                    <Badge bg="warning" className="me-3">Disabled</Badge>
                  )}
                  {product.variation?.salePrice ? (
                    <>
                      <span className="fw-bold text-danger me-2" style={{ fontSize: '1rem' }}>
                        {formatter.format(product.variation.salePrice)}
                      </span>
                      <span className="fw-bold text-muted text-decoration-line-through" style={{ fontSize: '0.9rem' }}>
                        {formatter.format(product.variation.price)}
                      </span>
                    </>
                  ) : (
                    <span className="fw-bold">{formatter.format(product.variation?.price)}</span>
                  )}
                  <i className="bi bi-chevron-right ms-2 fs-4"></i>
                </div>
              </ListGroup.Item>
            ))}
          </ListGroup>
        </div>
      ))}

      {/* Product Details Slide-in */}
      {selectedProduct && (
        <Offcanvas show={showSlideIn} onHide={() => setShowSlideIn(false)} placement="end">
        <Offcanvas.Header closeButton>
          <Offcanvas.Title className='me-5'>Update Product</Offcanvas.Title>
          <Button
            variant={selectedProduct.status === 'Active' ? 'secondary' : 'success'}
            onClick={handleStatusToggle}
          >
            {selectedProduct.status === 'Active' ? 'Disable' : 'Activate'}
          </Button>
        </Offcanvas.Header>
        <Offcanvas.Body>
          <div>
            <div className="d-flex justify-content-center mb-3">
              <img
                src={selectedProduct.variation?.image}
                alt={selectedProduct.name}
                style={{
                  width: '100px',
                  height: '100px',
                  objectFit: 'cover',
                }}
              />
            </div>
            <h5>{selectedProduct.name}</h5>
            {selectedProduct.variation?.strain && (
              <p className="text-muted" style={{ fontSize: '0.9rem' }}>
                {selectedProduct.variation.strain}
              </p>
            )}
            <p className="text-muted">{selectedProduct.description || 'No Description'}</p>
          </div>

          <Form className="mt-4">
            <Form.Group className="mb-3">
              <Form.Label>Price</Form.Label>
              <Form.Control
                type="number"
                value={updatedDetails.price}
                onChange={(e) => setUpdatedDetails({ ...updatedDetails, price: e.target.value })}
                placeholder="Enter price"
              />
            </Form.Group>

            <Form.Group className="mb-3">
              <Form.Label>Sale Price (Optional)</Form.Label>
              <Form.Control
                type="number"
                value={updatedDetails.salePrice || ''}
                onChange={(e) => setUpdatedDetails({ ...updatedDetails, salePrice: e.target.value })}
                placeholder="Enter sale price"
              />
            </Form.Group>

            <Form.Group className="mb-4">
              <Form.Label>Product Tags</Form.Label>
              <div className="d-flex flex-wrap gap-3">
                {availableTags.map((tag) => (
                  <Form.Check
                    key={tag}
                    type="checkbox"
                    id={`tag-${tag}`}
                    label={tag}
                    checked={updatedDetails.tags.includes(tag)}
                    onChange={() => handleTagToggle(tag)}
                  />
                ))}
              </div>
            </Form.Group>

            <div className="d-flex gap-3">
              <Button variant="primary" onClick={handleUpdate}>
                Save Changes
              </Button>
              <Button variant="danger" onClick={() => setShowDeleteModal(true)}>
                Delete Product
              </Button>
            </div>
          </Form>
        </Offcanvas.Body>
      </Offcanvas>
      )}

      {/* Delete Confirmation Modal */}
      <Modal show={showDeleteModal} onHide={() => setShowDeleteModal(false)}>
        <Modal.Header closeButton>
          <Modal.Title>Confirm Deletion</Modal.Title>
        </Modal.Header>
        <Modal.Body>Are you sure you want to delete this product?</Modal.Body>
        <Modal.Footer>
          <Button variant="secondary" onClick={() => setShowDeleteModal(false)}>
            Cancel
          </Button>
          <Button variant="danger" onClick={handleDelete}>
            Delete
          </Button>
        </Modal.Footer>
      </Modal>
    </div>
  );
};

export default InventoryComponent;
