import React, { useState, useContext } from 'react';
import { Button, Row, Col, Form } from 'react-bootstrap';
import { CartContext } from '../context/CartContext';

const PlaceOrder = () => {
  const { calculateTotal, cartItems } = useContext(CartContext);
  const [contactInfo, setContactInfo] = useState({
    firstName: '',
    lastName: '',
    day: '',
    month: '',
    year: '',
    email: '',
    phone: '',
  });

  const subtotal = calculateTotal();
  const taxRate = 0.17;
  const estimatedTotal = subtotal + subtotal * taxRate;

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setContactInfo({ ...contactInfo, [name]: value });
  };

  const handlePlaceOrder = async () => {
    if (!contactInfo.firstName || !contactInfo.lastName || !contactInfo.email || !contactInfo.phone) {
      alert('Please fill out all required fields.');
      return;
    }

    const orderDetails = {
      items: cartItems,
      contactInfo,
      subtotal: subtotal.toFixed(2),
      total: estimatedTotal.toFixed(2),
      orderDate: new Date().toISOString(),
    };

    try {
      const response = await fetch('/api/orders', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(orderDetails),
      });

      if (response.ok) {
        alert('Order placed successfully! Vendors have been notified.');
      } else {
        alert('Failed to place order. Please try again.');
      }
    } catch (error) {
      console.error('Error placing order:', error);
      alert('An error occurred. Please try again later.');
    }
  };

  return (
    <div className="place-order-section p-4 bg-light" style={{
        backgroundColor: '#f8f9fa',
        margin: '20px',
        borderRadius: '8px',
      }}>
      {/* Contact Information */}
      <h4>Contact Information</h4>
      <Form>
        {/* First Name and Last Name */}
        <Row className="mb-3">
          <Col>
            <Form.Label>First Name</Form.Label>
            <Form.Control
              type="text"
              name="firstName"
              value={contactInfo.firstName}
              onChange={handleInputChange}
              placeholder="Enter your first name"
              required
            />
          </Col>
          <Col>
            <Form.Label>Last Name</Form.Label>
            <Form.Control
              type="text"
              name="lastName"
              value={contactInfo.lastName}
              onChange={handleInputChange}
              placeholder="Enter your last name"
              required
            />
          </Col>
        </Row>

        {/* Date of Birth */}
        <Form.Group controlId="dob" className="mb-3">
          <Form.Label>Date of Birth</Form.Label>
          <Row>
            <Col>
              <Form.Control
                as="select"
                name="day"
                value={contactInfo.day}
                onChange={handleInputChange}
                required
              >
                <option value="">Day</option>
                {[...Array(31)].map((_, i) => (
                  <option key={i + 1} value={i + 1}>
                    {i + 1}
                  </option>
                ))}
              </Form.Control>
            </Col>
            <Col>
              <Form.Control
                as="select"
                name="month"
                value={contactInfo.month}
                onChange={handleInputChange}
                required
              >
                <option value="">Month</option>
                {[
                  'January',
                  'February',
                  'March',
                  'April',
                  'May',
                  'June',
                  'July',
                  'August',
                  'September',
                  'October',
                  'November',
                  'December',
                ].map((month, i) => (
                  <option key={i + 1} value={i + 1}>
                    {month}
                  </option>
                ))}
              </Form.Control>
            </Col>
            <Col>
              <Form.Control
                as="select"
                name="year"
                value={contactInfo.year}
                onChange={handleInputChange}
                required
              >
                <option value="">Year</option>
                {Array.from({ length: 100 }, (_, i) => (
                  <option key={i + 1955} value={i + 1955}>
                    {i + 1955}
                  </option>
                ))}
              </Form.Control>
            </Col>
          </Row>
        </Form.Group>

        {/* Email and Phone */}
        <Form.Group controlId="email" className="mb-3">
          <Form.Label>Email</Form.Label>
          <Form.Control
            type="email"
            name="email"
            value={contactInfo.email}
            onChange={handleInputChange}
            placeholder="Enter your email"
            required
          />
        </Form.Group>
        <Form.Group controlId="phone" className="mb-3">
          <Form.Label>Phone</Form.Label>
          <Form.Control
            type="text"
            name="phone"
            value={contactInfo.phone}
            onChange={handleInputChange}
            placeholder="Enter your phone number"
            required
          />
        </Form.Group>
      </Form>

      {/* Order Summary */}
      <h4 className="mt-4">Order Summary</h4>
      <Row className="mb-3">
        <Col><strong>Subtotal:</strong></Col>
        <Col className="text-end">${subtotal.toFixed(2)}</Col>
      </Row>
      <Row className="mb-3">
        <Col><strong>Estimated Total (including tax):</strong></Col>
        <Col className="text-end">${estimatedTotal.toFixed(2)}</Col>
      </Row>

      {/* Place Order Button and Message */}
      <Button variant="primary" className="w-100 mb-3" onClick={handlePlaceOrder}>
        Place Order
      </Button>
      <p className="text-muted text-center">
        Please note, vendor will be notified and you will receive delivery ETA shortly after placing your order.
      </p>
    </div>
  );
};

export default PlaceOrder;
