import React, { useState, useRef, useEffect } from 'react';
import { Modal, Form, Button } from 'react-bootstrap';

const AddressInput = ({ onAddressUpdate }) => {
  const [show, setShow] = useState(false);
  const [selectedAddress, setSelectedAddress] = useState(null);
  const inputRef = useRef(null);
  const autocompleteRef = useRef(null);

  const handleClose = () => setShow(false);
  const handleShow = () => setShow(true);

  useEffect(() => {
    if (window.google && inputRef.current) {
      const autocomplete = new window.google.maps.places.Autocomplete(inputRef.current, {
        types: ['geocode'], // Suggest addresses only
        componentRestrictions: { country: 'us' }, // Restrict to the US
      });

      // Listen for address selection
      autocomplete.addListener('place_changed', () => {
        const place = autocomplete.getPlace();
        if (place.geometry) {
          const location = {
            lat: place.geometry.location.lat(),
            lng: place.geometry.location.lng(),
            address: place.formatted_address,
          };

          setSelectedAddress(location.address); // Save selected address
          onAddressUpdate(location); // Pass location to parent
          handleClose(); // Close the modal
        }
      });

      autocompleteRef.current = autocomplete; // Save the autocomplete instance
    }
  }, []);

  return (
    <>
      {/* Main Display: Trigger Modal */}
      <div className="address-bar d-flex align-items-center">
        <i className="bi bi-geo-alt-fill me-2"></i>
        <span
          className="address-link"
          onClick={handleShow}
          style={{ textDecoration: 'underline', cursor: 'pointer' }}
        >
          {selectedAddress ? `Deliver to ${selectedAddress}` : 'Enter your address'}
        </span>
      </div>

      {/* Address Input Modal */}
      <Modal show={show} onHide={handleClose} centered>
        <Modal.Header closeButton>
          <Modal.Title>Enter Your Address</Modal.Title>
        </Modal.Header>
        <Modal.Body>
          <Form.Group controlId="addressInput">
            <Form.Control
              ref={inputRef}
              type="text"
              placeholder="Start typing your address..."
              aria-label="Enter your address"
            />
          </Form.Group>
          <div className="mt-3">
            {/* Optional: Display selected address */}
            {selectedAddress && (
              <div className="text-muted">
                <i className="bi bi-geo-alt-fill me-2"></i> Selected Address: <strong>{selectedAddress}</strong>
              </div>
            )}
          </div>
        </Modal.Body>
        <Modal.Footer>
          <Button variant="secondary" onClick={handleClose}>
            Cancel
          </Button>
        </Modal.Footer>
      </Modal>
    </>
  );
};

export default AddressInput;
