import React, { useEffect, useState } from 'react';

const VendorList = ({ address }) => {
  const [vendors, setVendors] = useState([]);

  useEffect(() => {
    if (address) {
      // Fetch vendors near the selected address
      const fetchNearbyVendors = async () => {
        const response = await fetch(`/api/vendors/nearby?address=${encodeURIComponent(address)}`);
        const data = await response.json();
        setVendors(data);
      };
      fetchNearbyVendors();
    }
  }, [address]);

  return (
    <div>
      {vendors.length ? (
        vendors.map((vendor) => (
          <div key={vendor._id} className="vendor-card">
            <img src={vendor.logoUrl} alt={`${vendor.dispensaryName} logo`} />
            <h3>{vendor.dispensaryName}</h3>
            <p>Rating: {vendor.rating || 'N/A'}</p>
            <p>{vendor.businessAddress.street}, {vendor.businessAddress.city}</p>
          </div>
        ))
      ) : (
        <p>No vendors available nearby.</p>
      )}
    </div>
  );
};

export default VendorList;
