import React, { useState } from 'react';

const AddressInput = ({ onAddressSelect }) => {
  const [input, setInput] = useState('');
  const [suggestions, setSuggestions] = useState([]);



  const handleInputChange = async (e) => {
    setInput(e.target.value);

    if (e.target.value.length > 2) {
      // Fetch suggestions using Google Maps Autocomplete API
      const response = await fetch(
        `https://maps.googleapis.com/maps/api/place/autocomplete/json?input=${encodeURIComponent(e.target.value)}&key=YOUR_GOOGLE_MAPS_API_KEY`
      );      
      const data = await response.json();
      setSuggestions(data.predictions);
    } else {
      setSuggestions([]);
    }
  };

  const handleSuggestionClick = (suggestion) => {
    setInput(suggestion.description);
    setSuggestions([]);
    onAddressSelect(suggestion.description);
  };

  return (
    <div>
      <input
        type="text"
        value={input}
        onChange={handleInputChange}
        placeholder="Enter your address"
      />
      <ul>
        {suggestions.map((suggestion) => (
          <li key={suggestion.place_id} onClick={() => handleSuggestionClick(suggestion)}>
            {suggestion.description} &gt;
          </li>
        ))}
      </ul>
    </div>
  );
};

export default AddressInput;
