import React, { useState, useEffect, useRef } from 'react';
import { Card, Button, Modal, Form, Badge } from 'react-bootstrap';
import axios from 'axios';

const DispensaryInfoCard = () => {
    const [dispensary, setDispensary] = useState({});
    const [showModal, setShowModal] = useState(false);
    const [formData, setFormData] = useState({
        dispensaryName: '',
        dispensaryType: '',
        license: '',
        storefrontAddress: '',
    });
    const [showAddressInput, setShowAddressInput] = useState(true);
    const [alert, setAlert] = useState({ message: '', variant: '' });
    const addressInputRef = useRef(null);
    const autocompleteRef = useRef(null);

    useEffect(() => {
        const fetchDispensaryData = async () => {
            try {
                const response = await axios.get('http://localhost:5001/api/vendors/profile', {
                    withCredentials: true,
                });
                setDispensary(response.data.data);
                setShowAddressInput(['Pickup', 'Pickup & Delivery'].includes(response.data.data.dispensaryType));
            } catch (error) {
                console.error('Error fetching dispensary details:', error);
                setAlert({ message: 'Failed to load dispensary details.', variant: 'danger' });
            }
        };
        fetchDispensaryData();
    }, []);

    useEffect(() => {
        if (showModal && showAddressInput && window.google && addressInputRef.current) {
            // Initialize Google Places Autocomplete
            autocompleteRef.current = new window.google.maps.places.Autocomplete(addressInputRef.current, {
                types: ['geocode'],
            });

            // Add place_changed listener
            const listener = autocompleteRef.current.addListener('place_changed', () => {
                const place = autocompleteRef.current.getPlace();
                if (place?.formatted_address && place?.geometry?.location) {
                    const formattedAddress = place.formatted_address;
                    const coordinates = [
                        place.geometry.location.lng(),
                        place.geometry.location.lat(),
                    ];
                    setFormData((prev) => ({
                        ...prev,
                        storefrontAddress: {
                            formatted: formattedAddress,
                            coordinates,
                            type: 'Point',
                        },
                    }));
                }
            });

            // Cleanup function to remove listener when component unmounts or modal closes
            return () => {
                if (autocompleteRef.current && listener) {
                    window.google.maps.event.removeListener(listener);
                }
                autocompleteRef.current = null;
            };
        }
    }, [showModal, showAddressInput]);

    const handleEdit = () => {
        setFormData({
            dispensaryName: dispensary.dispensaryName || '',
            dispensaryType: dispensary.dispensaryType || '',
            license: dispensary.license || '',
            storefrontAddress: dispensary.storefrontAddress?.formatted || '',
        });
        setShowModal(true);
    };

    const handleTypeChange = (type) => {
        setFormData(prev => ({ ...prev, dispensaryType: type }));
        setShowAddressInput(['Pickup', 'Pickup & Delivery'].includes(type));
    };

    const handleUpdate = async () => {
        try {
            const response = await axios.put(
                'http://localhost:5001/api/vendors/dispensary-info',
                {
                    ...formData,
                    storefrontAddress: formData.storefrontAddress,
                },
                { withCredentials: true }
            );

            if (response.data.success) {
                setDispensary(response.data.data);
                setAlert({ message: 'Dispensary information updated successfully!', variant: 'success' });
                setShowModal(false);
            }
        } catch (error) {
            console.error('Error updating dispensary details:', error);
            setAlert({ message: 'Failed to update details.', variant: 'danger' });
        }
    };

    const dispensaryTypes = ['Delivery', 'Pickup', 'Pickup & Delivery'];

    const modalStyle = {
        zIndex: '1050',
    };

    const addressInputStyle = {
        position: 'relative',
        zIndex: '1060',
    };

    return (
        <div>
            <Card className="shadow-sm" style={{ width: '100%' }}>
                <Card.Body className="p-3">
                    <div className="d-flex justify-content-between align-items-start">
                        <div>
                            <Card.Title className="fw-bold mb-2">
                                {dispensary.dispensaryName}
                            </Card.Title>
                            <Badge bg="light" text="dark" className="border mb-2">
                                {dispensary.dispensaryType || 'Type N/A'}
                            </Badge>
                            <div className="text-muted small mt-1">
                                License: {dispensary.license || 'LICENSE N/A'}
                            </div>
                            {dispensary.storefrontAddress?.formatted && (
                                <div className="text-muted small mt-1">
                                    {dispensary.storefrontAddress.formatted}
                                </div>
                            )}
                        </div>
                        <Button variant="outline-secondary" size="sm" onClick={handleEdit}>
                            Edit
                        </Button>
                    </div>
                </Card.Body>
            </Card>

            <Modal 
                show={showModal} 
                onHide={() => setShowModal(false)} 
                centered
                style={modalStyle}
            >
                <Modal.Header closeButton>
                    <Modal.Title>Edit Dispensary Information</Modal.Title>
                </Modal.Header>
                <Modal.Body>
                    <Form>
                        <Form.Group className="mb-3">
                            <Form.Label>Dispensary Name</Form.Label>
                            <Form.Control
                                type="text"
                                value={formData.dispensaryName}
                                onChange={(e) => setFormData(prev => ({ 
                                    ...prev, 
                                    dispensaryName: e.target.value 
                                }))}
                                placeholder="Enter dispensary name"
                            />
                        </Form.Group>

                        <Form.Group className="mb-3">
                            <Form.Label>Dispensary Type</Form.Label>
                            <div>
                                {dispensaryTypes.map((type) => (
                                    <Form.Check
                                        key={type}
                                        type="radio"
                                        label={type}
                                        name="dispensaryType"
                                        checked={formData.dispensaryType === type}
                                        onChange={() => handleTypeChange(type)}
                                        className="mb-2"
                                    />
                                ))}
                            </div>
                        </Form.Group>

                        <Form.Group className="mb-3">
                            <Form.Label>License Number</Form.Label>
                            <Form.Control
                                type="text"
                                value={formData.license}
                                onChange={(e) => setFormData(prev => ({ 
                                    ...prev, 
                                    license: e.target.value 
                                }))}
                                placeholder="Enter license number"
                            />
                        </Form.Group>

                        {showAddressInput && (
                            <Form.Group className="mb-3" style={addressInputStyle}>
                                <Form.Label>Storefront Address</Form.Label>
                                <Form.Control
                                    ref={addressInputRef}
                                    type="text"
                                    value={formData.storefrontAddress?.formatted || formData.storefrontAddress || ''}
                                    onChange={(e) => setFormData(prev => ({
                                        ...prev,
                                        storefrontAddress: e.target.value
                                    }))}
                                    placeholder="Enter storefront address"
                                />
                            </Form.Group>
                        )}
                    </Form>
                </Modal.Body>
                <Modal.Footer>
                    <Button variant="secondary" onClick={() => setShowModal(false)}>
                        Cancel
                    </Button>
                    <Button variant="primary" onClick={handleUpdate}>
                        Update
                    </Button>
                </Modal.Footer>
            </Modal>
        </div>
    );
};

export default DispensaryInfoCard;
