// src/components/ProfileUpdateForm.js
import React, { useState, useEffect } from 'react';
import { Form, Button, Alert } from 'react-bootstrap';
import api from '../api/api'; // Assuming api.js is configured for Axios requests

const ProfileUpdateForm = () => {
    const [profile, setProfile] = useState({
        firstName: '',
        lastName: '',
        dispensaryName: '',
        address: '',
        contactNumber: '',
        storeLogo: '',
        storeNotice: '',
        status: 'active',
        posSystem: '',
        apiKey: ''
    });
    const [message, setMessage] = useState({ type: '', text: '' });

    // Fetch vendor's current profile on component mount
    useEffect(() => {
        const fetchProfile = async () => {
            try {
                const response = await api.get('/vendors/profile'); // Assumes the route is protected by a token
                if (response.data.success) {
                    setProfile(response.data.data);
                } else {
                    setMessage({ type: 'error', text: 'Failed to load profile data' });
                }
            } catch (error) {
                console.error("Error fetching profile data:", error);
                setMessage({ type: 'error', text: 'Error loading profile data' });
            }
        };
        fetchProfile();
    }, []);

    // Handle input changes
    const handleChange = (e) => {
        const { name, value } = e.target;
        setProfile({ ...profile, [name]: value });
    };

    // Handle profile update form submission
    const handleSubmit = async (e) => {
        e.preventDefault();
        try {
            const response = await api.put('/vendors/profile', profile);
            if (response.data.success) {
                setMessage({ type: 'success', text: 'Profile updated successfully!' });
            } else {
                setMessage({ type: 'error', text: 'Failed to update profile' });
            }
        } catch (error) {
            console.error("Error updating profile:", error);
            setMessage({ type: 'error', text: 'Error updating profile' });
        }
    };

    return (
        <div className="mt-3">
            <h4>Update Profile</h4>
            {message.text && (
                <Alert variant={message.type === 'success' ? 'success' : 'danger'}>
                    {message.text}
                </Alert>
            )}
            <Form onSubmit={handleSubmit}>
                <Form.Group controlId="firstName" className="mb-3">
                    <Form.Label>First Name</Form.Label>
                    <Form.Control
                        type="text"
                        name="firstName"
                        value={profile.firstName}
                        onChange={handleChange}
                    />
                </Form.Group>

                <Form.Group controlId="lastName" className="mb-3">
                    <Form.Label>Last Name</Form.Label>
                    <Form.Control
                        type="text"
                        name="lastName"
                        value={profile.lastName}
                        onChange={handleChange}
                    />
                </Form.Group>

                <Form.Group controlId="dispensaryName" className="mb-3">
                    <Form.Label>Dispensary Name</Form.Label>
                    <Form.Control
                        type="text"
                        name="dispensaryName"
                        value={profile.dispensaryName}
                        onChange={handleChange}
                    />
                </Form.Group>

                <Form.Group controlId="address" className="mb-3">
                    <Form.Label>Address</Form.Label>
                    <Form.Control
                        type="text"
                        name="address"
                        value={profile.address}
                        onChange={handleChange}
                    />
                </Form.Group>

                <Form.Group controlId="contactNumber" className="mb-3">
                    <Form.Label>Contact Number</Form.Label>
                    <Form.Control
                        type="text"
                        name="contactNumber"
                        value={profile.contactNumber}
                        onChange={handleChange}
                    />
                </Form.Group>

                <Form.Group controlId="storeLogo" className="mb-3">
                    <Form.Label>Store Logo URL</Form.Label>
                    <Form.Control
                        type="text"
                        name="storeLogo"
                        value={profile.storeLogo}
                        onChange={handleChange}
                    />
                </Form.Group>

                <Form.Group controlId="storeNotice" className="mb-3">
                    <Form.Label>Store Notice</Form.Label>
                    <Form.Control
                        type="text"
                        name="storeNotice"
                        value={profile.storeNotice}
                        onChange={handleChange}
                    />
                </Form.Group>

                <Form.Group controlId="status" className="mb-3">
                    <Form.Label>Status</Form.Label>
                    <Form.Select
                        name="status"
                        value={profile.status}
                        onChange={handleChange}
                    >
                        <option value="active">Active</option>
                        <option value="inactive">Inactive</option>
                    </Form.Select>
                </Form.Group>

                <Form.Group controlId="posSystem" className="mb-3">
                    <Form.Label>POS System</Form.Label>
                    <Form.Control
                        type="text"
                        name="posSystem"
                        value={profile.posSystem}
                        onChange={handleChange}
                    />
                </Form.Group>

                <Form.Group controlId="apiKey" className="mb-3">
                    <Form.Label>POS API Key</Form.Label>
                    <Form.Control
                        type="text"
                        name="apiKey"
                        value={profile.apiKey}
                        onChange={handleChange}
                    />
                </Form.Group>

                <Button type="submit" variant="primary" className="mt-2">
                    Save Changes
                </Button>
            </Form>
        </div>
    );
};

export default ProfileUpdateForm;
