import React, { useContext } from 'react';
import { FaPhone, FaInfoCircle, FaRegThumbsUp } from 'react-icons/fa';
import { VendorContext } from './context/VendorContext';

const StoreDetails = () => {
    const { vendor } = useContext(VendorContext);
    const isAvailable = vendor.status === 'active';

    // Toggle visibility of the info tooltip for store hours and policy


    const toggleInfo = () => setShowInfo(!showInfo);

    return (
        <div className="d-flex align-items-center my-4">
            {/* Vendor Logo */}
            <img
                src={vendor.storeLogo}
                alt={`${vendor.dispensaryName} logo`}
                style={{
                    width: '90px',
                    height: '90px',
                    borderRadius: '50%',
                    objectFit: 'cover',
                    marginRight: '20px',
                }}
            />

            {/* Vendor Info Column */}
            <div className="d-flex flex-column">
                {/* Row 1: Dispensary Type Badge */}
                <span
                    className="badge bg-light text-dark mb-2"
                    style={{ fontSize: '0.75rem', padding: '0.3rem 0.6rem' }}
                >
                    Rec
                </span>

                {/* Row 2: Dispensary Name and Rating */}
                <h4 className="mb-1 d-flex align-items-center">
                    {vendor.dispensaryName}
                    <span className="ms-2 text-muted d-flex align-items-center" style={{ fontSize: '0.9rem' }}>
                        <FaRegThumbsUp className="me-1" /> {vendor.rating}%
                    </span>
                </h4>

                {/* Row 3: City, Delivery Time, and Minimum Order */}
                <div className="text-muted mb-2" style={{ fontSize: '0.85rem' }}>
                    {vendor.state} | 30-60min | $40 min
                </div>

                {/* Row 4: Status, Call Button, and Info Button */}
                <div className="d-flex align-items-center">
                    {/* Status Circle */}
                    <div
                        style={{
                            width: '10px',
                            height: '10px',
                            borderRadius: '50%',
                            backgroundColor: isAvailable ? 'green' : 'red',
                            marginRight: '5px',
                        }}
                    ></div>
                    <small className="text-muted me-4">
                        {isAvailable ? 'Available' : 'Unavailable'}
                    </small>

                    {/* Call Button */}
                    <button
                        className="btn btn-light btn-sm me-3 d-flex align-items-center"
                        onClick={() => window.open(`tel:${vendor.contactNumber}`, '_self')}
                    >
                        <FaPhone className="me-1" /> Call
                    </button>

                    {/* Info Button */}
                    <button
                        className="btn btn-white btn-sm"
                        onClick={toggleInfo}
                        style={{ position: 'relative' }}
                    >
                        <FaInfoCircle />
                    </button>

                    {/* Info Tooltip for Store Hours and Policy */}
                    {showInfo && (
                        <div
                            style={{
                                position: 'absolute',
                                top: '100%',
                                left: '50%',
                                transform: 'translateX(-50%)',
                                backgroundColor: 'white',
                                border: '1px solid #ccc',
                                padding: '10px',
                                borderRadius: '5px',
                                boxShadow: '0px 4px 8px rgba(0, 0, 0, 0.1)',
                                width: '200px',
                                zIndex: 1,
                            }}
                        >
                            <p style={{ fontSize: '0.85rem', margin: 0 }}>{vendor.storeNotice || 'Store hours and policy information not available.'}</p>
                        </div>
                    )}
                </div>
            </div>
        </div>
    );
};

export default StoreDetails;
