import React, { useEffect, useRef } from 'react';

const PickupMap = ({ dispensaries, onMarkerClick }) => {
  const mapRef = useRef(null);
  const mapInstance = useRef(null);
  const markers = useRef([]);

  // Function to load Google Maps script dynamically
  const loadGoogleMapsScript = (callback) => {
    const existingScript = document.getElementById('google-maps-script');

    if (!existingScript) {
      const script = document.createElement('script');
      script.src = `https://maps.googleapis.com/maps/api/js?key=${process.env.REACT_APP_GOOGLE_MAPS_API_KEY}`;
      script.id = 'google-maps-script';
      script.async = true;
      script.defer = true;
      script.onload = callback;
      document.body.appendChild(script);
    } else {
      existingScript.onload = callback;
    }
  };

  // Initialize map
  const initializeMap = () => {
    if (mapRef.current && !mapInstance.current) {
      mapInstance.current = new window.google.maps.Map(mapRef.current, {
        center: { lat: 34.0522, lng: -118.2437 }, // Default center (Los Angeles)
        zoom: 12,
      });
    }
  };

  // Add markers to the map
  const addMarkers = () => {
    if (!mapInstance.current || !dispensaries.length) return;

    // Clear previous markers
    markers.current.forEach((marker) => marker.setMap(null));
    markers.current = [];

    // Add new markers
    dispensaries.forEach((dispensary) => {
      if (!dispensary.deliveryZone?.coordinates) return;

      const [lng, lat] = dispensary.deliveryZone.coordinates;

      const marker = new window.google.maps.Marker({
        position: { lat, lng },
        map: mapInstance.current,
        title: dispensary.dispensaryName,
      });

      marker.addListener('click', () => {
        if (onMarkerClick) onMarkerClick(dispensary); // Trigger parent callback
      });

      markers.current.push(marker);
    });
  };

  useEffect(() => {
    loadGoogleMapsScript(() => {
      if (window.google) {
        initializeMap();
      } else {
        console.error('Google Maps JavaScript API failed to load.');
      }
    });
  }, []);

  useEffect(() => {
    addMarkers();
  }, [dispensaries]);

  return <div ref={mapRef} style={{ width: '100%', height: '500px' }}></div>;
};

export default PickupMap;
