import React, { useState, useContext } from 'react';
import { CartContext } from '../context/CartContext';
import ProductDetails from './ProductDetails'; // Import the ProductDetails component

const ProductCard = ({ product, vendorName, logoUrl, vendorId }) => {
  const { addToCart } = useContext(CartContext);
  const [showDetails, setShowDetails] = useState(false);

  const handleDetailsOpen = () => setShowDetails(true);
  const handleDetailsClose = () => setShowDetails(false);

  // Safeguard values
  const price = product.price ?? 0;
  const salePrice = product.salePrice ?? null;
  const image = product.image || '/placeholder-image.png';
  const amount = product.amount;

  return (
    <>
      <div
        className="card product-card shadow-sm"
        style={{
          width: '14rem',
          position: 'relative',
          cursor: 'default', // Prevent unintended cursor style on the card
          paddingTop: salePrice ? '2rem' : '0',
        }}
      >
        {/* SALE Banner */}
        {salePrice && (
          <div
            className="position-absolute top-0 start-0 w-100 text-center rounded-top"
            style={{
              backgroundColor: '#FF0080',
              color: 'white',
              fontSize: '0.8rem',
              padding: '0.3rem 0',
              fontWeight: 'bold',
            }}
          >
            SALE
          </div>
        )}

        {/* Card Body */}
        <div className="card-body d-flex flex-column justify-content-between">
          {/* Product Info Header */}
          <div className="d-flex justify-content-between">
            <span className="text-muted mb-2" style={{ fontSize: '0.8rem' }}>
              {product.category || 'Uncategorized'}
            </span>
            <span
              className="badge border-none text-secondary"
              style={{
                fontSize: '0.8rem',
                fontWeight: '500',
                padding: '0.2rem 0.5rem',
                borderRadius: '0.25rem',
              }}
            >
              {amount || 'N/A'}
            </span>
          </div>

          {/* Product Image */}
          <div className="text-center my-1">
            <img
              src={image}
              alt={product.name || 'Product Image'}
              className="img-fluid"
              style={{ maxHeight: '120px', objectFit: 'contain', cursor: 'pointer' }}
              onClick={handleDetailsOpen} // Open modal when clicking image
            />
          </div>

          {/* Product Info */}
          <div className="text-start">
            <p
              className="fw-bold text-center mb-1"
              style={{ fontSize: '0.8rem', cursor: 'pointer' }}
              onClick={handleDetailsOpen} // Open modal when clicking name
            >
              {product.brand || 'No Brand'}
            </p>
            <p
              className="text-dark text-center mb-1"
              style={{ fontSize: '1rem', fontWeight: 'normal', cursor: 'pointer' }}
              onClick={handleDetailsOpen} // Open modal when clicking name
            >
              {product.name}
            </p>
            <p
              className="text-muted text-center mb-1"
              style={{ fontSize: '.85rem', fontWeight: 'normal', cursor: 'pointer' }}
              onClick={handleDetailsOpen} // Open modal when clicking strain
            >
              {strain}
            </p>
          </div>

          {/* Footer Section */}
          <div className="d-flex justify-content-between align-items-center mt-2">
            {/* Price Section */}
            <div
              className="d-flex align-items-center justify-content-center mt-2"
              style={{ gap: '0.5rem' }}
            >
              {salePrice ? (
                <>
                  <span
                    className="badge bg-light"
                    style={{
                      fontSize: '1.1rem',
                      padding: '0.5rem 1rem',
                      fontWeight: 'bold',
                      color: '#FF0080',
                      borderRadius: '0.5rem',
                    }}
                  >
                    ${salePrice.toFixed(2)}
                  </span>
                  <span
                    style={{
                      fontSize: '0.78rem',
                      textDecoration: 'line-through',
                      color: '#888',
                      fontWeight: 'normal',
                    }}
                  >
                    ${price.toFixed(2)}
                  </span>
                </>
              ) : (
                <span
                  className="badge bg-light text-dark"
                  style={{
                    fontSize: '1.1rem',
                    padding: '0.5rem 1rem',
                    fontWeight: 'bold',
                    borderRadius: '0.5rem',
                  }}
                >
                  ${price.toFixed(2)}
                </span>
              )}
            </div>

            {/* Add to Cart Button */}
            <button
              className="btn btn-primary rounded-circle"
              style={{
                width: '2.5rem',
                height: '2.5rem',
                display: 'flex',
                alignItems: 'center',
                justifyContent: 'center',
                fontSize: '1.5rem',
                fontWeight: 'bold',
              }}
              onClick={(e) => {
                e.stopPropagation(); // Prevent opening details modal when clicking "Add to Cart"
                addToCart({
                  ...product,
                  price: price,
                  salePrice: salePrice,
                  vendorName,
                  logoUrl,
                  primaryImage: image,
                  strain: strain,
                  vendorId,
                });
              }}
            >
              +
            </button>
          </div>
        </div>
      </div>

      {/* Product Details Modal */}
      <ProductDetails
        show={showDetails}
        handleClose={handleDetailsClose}
        product={product}
        vendorName={vendorName}
        logoUrl={logoUrl}
        vendorId={vendorId}
      />
    </>
  );
};

export default ProductCard;
