import React from 'react';
import { Container, Row, Col } from 'react-bootstrap';
import ProductCard from './ProductCard';

const MenuDisplay = ({ products }) => {
  if (!products || products.length === 0) {
    return <p>No products available.</p>; // Handle empty or undefined products
  }

  return (
    <div
      style={{
        backgroundColor: '#FDFDFD', // Light grey background
        padding: '2rem 1rem', // Padding around the container (reduced horizontal padding)
        borderRadius: '8px', // Rounded corners for a polished look
        position: 'relative', // Proper positioning context
        overflow: 'hidden',
      }}
    >
      <Container fluid style={{ paddingLeft: '15px', paddingRight: '15px' }}>
        <Row>
          {products.map((product) => (
            <Col
              key={product._id}
              xs={12}
              sm={6}
              md={4}
              lg={3}
              className="mb-3" // Add bottom margin to each card
            >
              <ProductCard product={product} />
            </Col>
          ))}
        </Row>
      </Container>
    </div>
  );
};

export default MenuDisplay;
