import React, { useState } from 'react';

const LoginPage = () => {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState('');
  const [loading, setLoading] = useState(false);

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError('');
    setLoading(true);

    try {
      // Simulating API call
      await new Promise(resolve => setTimeout(resolve, 1000));
      // Add your actual login logic here
    } catch (err) {
      console.error('Login error:', err);
      setError('Login failed. Please check your credentials.');
    } finally {
      setLoading(false);
    }
  };

  const pageStyle = {
    backgroundColor: '#fafafa',
    minHeight: '100vh',
    display: 'flex',
    alignItems: 'center',
    paddingTop: '20px',
    paddingBottom: '20px'
  };

  const cardStyle = {
    maxWidth: '400px',
    margin: '0 auto',
    padding: '20px',
    boxShadow: '0 0 10px rgba(0,0,0,0.1)',
    borderRadius: '8px',
    backgroundColor: 'white',
    width: '100%'
  };

  const inputStyle = {
    width: '100%',
    padding: '8px 12px',
    marginBottom: '10px',
    border: '1px solid #ced4da',
    borderRadius: '4px',
    fontSize: '16px'
  };

  const buttonStyle = {
    width: '100%',
    padding: '10px',
    backgroundColor: '#191919',
    color: 'white',
    border: 'none',
    borderRadius: '4px',
    cursor: 'pointer',
    fontSize: '16px'
  };

  const labelStyle = {
    display: 'block',
    marginBottom: '5px',
    fontSize: '14px',
    fontWeight: '500'
  };

  const errorStyle = {
    padding: '10px',
    marginBottom: '15px',
    backgroundColor: '#f8d7da',
    border: '1px solid #f5c2c7',
    borderRadius: '4px',
    color: '#842029'
  };

  const linkStyle = {
    display: 'block',
    marginTop: '15px',
    textAlign: 'center',
    color: '#191919',
    textDecoration: 'none'
  };

  return (
    <div style={pageStyle}>
      <div className="container">
        <div style={cardStyle}>
          <h3 style={{ textAlign: 'center', marginBottom: '20px' }}>Vendor Login</h3>

          {error && (
            <div style={errorStyle}>
              {error}
            </div>
          )}

          <form onSubmit={handleSubmit}>
            <div style={{ marginBottom: '15px' }}>
              <label htmlFor="email" style={labelStyle}>
                Email
              </label>
              <input
                type="email"
                id="email"
                style={inputStyle}
                placeholder="Enter your email"
                value={email}
                onChange={(e) => setEmail(e.target.value)}
                required
              />
            </div>

            <div style={{ marginBottom: '15px' }}>
              <label htmlFor="password" style={labelStyle}>
                Password
              </label>
              <input
                type="password"
                id="password"
                style={inputStyle}
                placeholder="Enter your password"
                value={password}
                onChange={(e) => setPassword(e.target.value)}
                required
              />
            </div>

            <button
              type="submit"
              style={{
                ...buttonStyle,
                opacity: loading ? 0.7 : 1,
                cursor: loading ? 'not-allowed' : 'pointer'
              }}
              disabled={loading}
            >
              {loading ? 'Logging in...' : 'Sign In'}
            </button>

            <a href="/reset-password-request" style={linkStyle}>
              Forgot Password?
            </a>
          </form>
        </div>
      </div>
    </div>
  );
};

export default LoginPage;
