import React, { useEffect, useState } from 'react';

const ProductList = () => {
    const [products, setProducts] = useState([]); // Initialize products as an empty array
    const [token, setToken] = useState('');
    const [error, setError] = useState('');

    // Function to get access token
    const getAccessToken = async () => {
        const options = {
            method: 'POST',
            headers: {
                accept: 'application/json',
                'content-type': 'application/x-www-form-urlencoded'
            },
            body: new URLSearchParams({
                client_id: 'wNzewkHXh2P5GnfCFPX3AmeA',
                apikey: 'MTQ5Mjg2ODdjMDBmNTliZDUxZ'
            })
        };

        try {
            const response = await fetch(
                'https://api.treez.io/v2.0/dispensary/partnersandbox2/config/api/gettokens',
                options
            );
            const data = await response.json();
            if (data.resultCode === 'SUCCESS' && data.access_token) {
                setToken(data.access_token);
            } else {
                throw new Error('Failed to get access token');
            }
        } catch (err) {
            setError(`Error getting token: ${err.message}`);
        }
    };

    // Function to fetch products using the access token
    const fetchProducts = async (accessToken) => {
        const options = {
            method: 'GET',
            headers: {
                Authorization: `Bearer ${accessToken}`,
                'Content-Type': 'application/json'
            }

        };
        console.log("Access Token:", token);


        try {
            const response = await fetch('http://localhost:4000/api/products?page=1', options); // Updated to port 4000
            if (response.status === 403) {
                throw new Error('Access denied: Check API permissions or access token validity.');
            } else if (response.status >= 500) {
                throw new Error('Server error: Please check proxy server configuration or API server status.');
            }

            const data = await response.json();
            if (data && data.data) {
                setProducts(data.data); // Set products if data is available
            } else {
                throw new Error('Product data not found in response');
            }
        } catch (err) {
            setError(`Error fetching products: ${err.message}`);
        }
    };

    // Initialize access token on component mount
    useEffect(() => {
        const initialize = async () => {
            await getAccessToken();
        };
        initialize();
    }, []);

    // Fetch products once the token is set
    useEffect(() => {
        if (token) {
            fetchProducts(token);
        }
    }, [token]);

    return (
        <div className="container my-4">
            <h2>Product List</h2>
            {error && <p className="text-danger">{error}</p>}
            <div className="row">
                {products && products.length > 0 ? (
                    products.map((product) => (
                        <div className="col-md-4 mb-3" key={product.product_id}>
                            <div className="card">
                                <img
                                    src={product.e_commerce?.primary_image || 'https://via.placeholder.com/150'}
                                    alt={product.product_configurable_fields.name}
                                    className="card-img-top"
                                />
                                <div className="card-body">
                                    <h5 className="card-title">{product.product_configurable_fields.name}</h5>
                                    <p className="card-text">Price: ${product.pricing.price_sell}</p>
                                    <p className="card-text">Category: {product.category_type}</p>
                                </div>
                            </div>
                        </div>
                    ))
                ) : (
                    <p>No products available</p>
                )}
            </div>
        </div>
    );
};

export default ProductList;
