import React, { useState } from 'react';
import { Form, Button } from 'react-bootstrap';

const MinOrderComponent = ({ minOrder, onUpdate }) => {
  const [newMinOrder, setNewMinOrder] = useState(minOrder || 0);

  const handleSubmit = (e) => {
    e.preventDefault();
    if (onUpdate) {
      onUpdate(newMinOrder); // Call the provided onUpdate function
    }
  };

  return (
    <Form onSubmit={handleSubmit}>
      <Form.Group>
        <Form.Label>Minimum Order Amount</Form.Label>
        <Form.Control
          type="number"
          value={newMinOrder}
          onChange={(e) => setNewMinOrder(e.target.value)}
          min={0}
        />
      </Form.Group>
      <Button type="submit" variant="primary" className="mt-3">
        Update Minimum Order
      </Button>
    </Form>
  );
};

export default MinOrderComponent;
