import React, { useState, useEffect } from 'react';
import { useAuth } from '../context/AuthContext'; // Import the AuthContext hook

const AddProductPage = () => {
  const { auth } = useAuth(); // Access vendor details from the context
  const [brand, setBrand] = useState('');
  const [category, setCategory] = useState('Flower'); // Default category
  const [products, setProducts] = useState([]);
  const [selectedProduct, setSelectedProduct] = useState(null);
  const [selectedAmount, setSelectedAmount] = useState('');
  const [availableStrains, setAvailableStrains] = useState([]);
  const [price, setPrice] = useState('');
  const [salePrice, setSalePrice] = useState('');
  const [stock, setStock] = useState('');
  const [selectedImage, setSelectedImage] = useState('');
  const [selectedStrain, setSelectedStrain] = useState('');

  // Fetch products by brand and category
  useEffect(() => {
    if (brand) {
      const queryParams = new URLSearchParams({ brand, category }).toString();
      fetch(`/api/products/filters?${queryParams}`)
        .then((response) => response.json())
        .then((data) => setProducts(data))
        .catch((error) => console.error('Error fetching products:', error));
    } else {
      setProducts([]);
    }
  }, [brand, category]);

  const handleProductSelect = (product) => {
    setSelectedProduct(product);
    setSelectedImage(product.images?.[0] || '');
    setSelectedAmount('');
    setAvailableStrains([]);
    setSelectedStrain('');
  };

  const handleAmountSelect = (amount) => {
    setSelectedAmount(amount);
    if (selectedProduct) {
      const strains = selectedProduct.variations
        .filter((variation) => variation.amount === amount)
        .map((variation) => variation.strain);
      setAvailableStrains(strains);
      setSelectedStrain(''); // Reset strain selection
    }
  };

  const handleImageSelect = (image) => {
    setSelectedImage(image);
  };

  const handleAddToInventory = async () => {
    if (!selectedProduct || !selectedAmount || !selectedStrain || !price || !stock) {
      alert('Please fill out all required fields.');
      return;
    }

    // Find the full variation object based on selected strain and amount
    const selectedVariation = selectedProduct.variations.find(
      (variation) =>
        variation.amount === selectedAmount && variation.strain === selectedStrain
    );

    if (!selectedVariation) {
      alert('Selected variation not found.');
      return;
    }

    // Add price, salePrice, stock, and image to the selected variation
    const variationWithDetails = {
      ...selectedVariation,
      price: parseFloat(price),
      salePrice: salePrice ? parseFloat(salePrice) : undefined, // Include salePrice only if provided
      stock: parseInt(stock, 10),
      image: selectedImage,
    };

    try {
      const response = await fetch('/api/vendor/inventory', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({
          vendorId: auth.vendorId,
          productId: selectedProduct._id,
          variation: variationWithDetails, // Pass the variation object with all details
          name: selectedProduct.name,
          category: selectedProduct.category,
          brand: selectedProduct.brand,
          description: selectedProduct.description,
        }),
      });

      const data = await response.json();

      if (response.ok) {
        alert(data.message);
        // Reset form
        setBrand('');
        setCategory('Flower');
        setSelectedProduct(null);
        setSelectedAmount('');
        setAvailableStrains([]);
        setPrice('');
        setSalePrice('');
        setStock('');
        setSelectedImage('');
        setSelectedStrain('');
      } else {
        alert(data.message || 'Failed to add product to inventory.');
      }
    } catch (error) {
      console.error('Error adding product to inventory:', error);
      alert('An error occurred while adding the product.');
    }
  };


  return (
    <div className="container mt-4">
      <h1>Add Product to Inventory</h1>
      <div className="mb-3">
        <label className="form-label">Category</label>
        <select
          className="form-select"
          value={category}
          onChange={(e) => setCategory(e.target.value)}
        >
          <option value="Flower">Flower</option>
          <option value="Pre-roll">Pre-roll</option>
          <option value="Vape">Vape</option>
          <option value="Edible">Edible</option>
          <option value="Extract">Extract</option>
          <option value="Topical">Topical</option>
          <option value="Gear">Gear</option>
        </select>
      </div>

      <div className="mb-3">
        <label className="form-label">Brand</label>
        <input
          type="text"
          className="form-control"
          placeholder="Enter brand name"
          value={brand}
          onChange={(e) => setBrand(e.target.value)}
        />
      </div>

      {products.length > 0 && (
        <div className="mb-3">
          <label className="form-label">Matching Products</label>
          <ul className="list-group">
            {products.map((product) => (
              <li
                key={product._id}
                className={`list-group-item ${selectedProduct?._id === product._id ? 'active' : ''}`}
                onClick={() => handleProductSelect(product)}
                style={{ cursor: 'pointer' }}
              >
                <strong>{product.brand}</strong> - {product.name}{' '}
                {product.images?.[0] && (
                  <img
                    src={product.images[0]}
                    alt={product.name}
                    style={{ width: '40px', marginLeft: '10px' }}
                  />
                )}
              </li>
            ))}
          </ul>
        </div>
      )}

      {selectedProduct && (
        <>
          <div className="mb-3">
            <label className="form-label">Select Amount</label>
            <select
              className="form-select"
              value={selectedAmount}
              onChange={(e) => handleAmountSelect(e.target.value)}
            >
              <option value="" disabled>
                Select amount
              </option>
              {[...new Set(selectedProduct.variations.map((variation) => variation.amount))].map(
                (amount, index) => (
                  <option key={index} value={amount}>
                    {amount}
                  </option>
                )
              )}
            </select>
          </div>

          {selectedProduct.images && (
            <div className="mb-3">
              <label className="form-label">Select Image</label>
              <div className="d-flex overflow-auto" style={{ maxWidth: '100%', gap: '10px' }}>
                {selectedProduct.images.map((image, index) => (
                  <img
                    key={index}
                    src={image}
                    alt={`Product Image ${index + 1}`}
                    onClick={() => handleImageSelect(image)}
                    className={`img-thumbnail ${selectedImage === image ? 'border-primary' : ''}`}
                    style={{
                      cursor: 'pointer',
                      width: '80px',
                      height: '80px',
                      objectFit: 'cover',
                      borderWidth: selectedImage === image ? '3px' : '1px',
                    }}
                  />
                ))}
              </div>
            </div>
          )}

          {availableStrains.length > 0 && (
            <div className="mb-3">
              <label className="form-label">Select Strain</label>
              <select
                className="form-select"
                value={selectedStrain}
                onChange={(e) => setSelectedStrain(e.target.value)}
              >
                <option value="" disabled>
                  Select a strain
                </option>
                {availableStrains.map((strain, index) => (
                  <option key={index} value={strain}>
                    {strain}
                  </option>
                ))}
              </select>
            </div>
          )}

          <div className="mb-3">
            <label className="form-label">Price</label>
            <input
              type="number"
              className="form-control"
              placeholder="Enter price"
              value={price}
              onChange={(e) => setPrice(e.target.value)}
            />
          </div>

          <div className="mb-3">
            <label className="form-label">Stock</label>
            <input
              type="number"
              className="form-control"
              placeholder="Enter stock"
              value={stock}
              onChange={(e) => setStock(e.target.value)}
            />
          </div>

          <button className="btn btn-success" onClick={handleAddToInventory}>
            Add to Inventory
          </button>
        </>
      )}
    </div>
  );
};

export default AddProductPage;
