import React, { useState } from 'react';
import { Card, Row, Col, Image, Badge, Button, Modal } from 'react-bootstrap';
import PickupDeliveryToggle from './PickupDeliveryToggle'; // Ensure this component exists
import VendorStatusDropdown from './VendorStatusDropdown'; // Ensure this component exists
import './VendorDetails.css'; // Import CSS file for additional styling

const VendorDetails = ({ vendor }) => {
  const [showPromoModal, setShowPromoModal] = useState(false);

  if (!vendor || !vendor.businessHours) {
    return <p>Loading vendor details...</p>; // Handle uninitialized vendor object
  }

  // Determine vendor open/close status
  const getVendorStatus = () => {
    const currentDay = new Date().toLocaleString('en-US', { weekday: 'long' }).toLowerCase();
    const currentTime = new Date();
    const openTime = new Date(
      `${currentDay} ${vendor.businessHours[currentDay]?.open || '00:00'}`
    );
    const closeTime = new Date(
      `${currentDay} ${vendor.businessHours[currentDay]?.close || '23:59'}`
    );

    if (currentTime >= openTime && currentTime <= closeTime) {
      return { status: 'open', until: vendor.businessHours[currentDay]?.close };
    }

    // Find next opening day
    const days = Object.keys(vendor.businessHours);
    const nextDayIndex = (days.indexOf(currentDay) + 1) % days.length;
    const nextDay = days[nextDayIndex];
    return { status: 'closed', until: vendor.businessHours[nextDay]?.open };
  };

  const vendorStatus = getVendorStatus();

  return (
    <Card
      className="vendor-details p-3 my-2"
      style={{
        backgroundColor: '#fafafa', // Light background
        border: '1px solid #ddd',
        borderRadius: '15px 15px 0 0', // Rounded top corners only
      }}
    >
      <Row className="align-items-center">
        {/* Vendor Logo */}
        <Col xs={3} className="d-flex justify-content-start">
          <Image
            src={vendor.logoUrl || '/placeholder-logo.png'}
            alt={`${vendor.dispensaryName} logo`}
            className="rounded-circle"
            style={{ width: '60px', height: '60px', objectFit: 'cover' }}
          />
        </Col>

        {/* PickupDeliveryToggle */}
        <Col xs={9} className="d-flex justify-content-end">
          <PickupDeliveryToggle />
        </Col>
      </Row>

      {/* Vendor Details */}
      <Row className="mt-3">
        <Col>
          <h5 className="font-semibold mb-2">{vendor.dispensaryName}</h5>

          {/* Vendor Status */}
          <div className="d-flex align-items-center mb-2">
            <span
              className={`status-circle ${
                vendorStatus.status === 'open' ? 'bg-success' : 'bg-danger'
              }`}
              style={{
                display: 'inline-block',
                width: '12px',
                height: '12px',
                borderRadius: '50%',
                marginRight: '8px',
              }}
            ></span>
            <span>
              {vendorStatus.status === 'open'
                ? `Open until ${vendorStatus.until} pm`
                : `Closed at ${vendorStatus.until} am`}
            </span>
            <VendorStatusDropdown vendor={vendor} />
          </div>

          {/* Payment Method Badges */}
          <div className="mb-3">
            {vendor.acceptedPayments.map((payment, index) => (
              <Badge
                key={index}
                bg="info"
                className="me-1"
                style={{ borderRadius: '20px' }}
              >
                {payment.method}
              </Badge>
            ))}
          </div>

          {/* Daily Promo Button */}
          {vendor.dailyPromo?.title && (
            <Button
              variant="outline-primary"
              onClick={() => setShowPromoModal(true)}
              className="d-flex align-items-center"
              style={{ borderRadius: '30px' }}
            >
              <i className="bi bi-tag me-2"></i>
              {vendor.dailyPromo.title.slice(0, 10)}...
            </Button>
          )}
        </Col>
      </Row>

      {/* Promo Modal */}
      <Modal show={showPromoModal} onHide={() => setShowPromoModal(false)}>
        <Modal.Header closeButton>
          <Modal.Title>{vendor.dailyPromo?.title}</Modal.Title>
        </Modal.Header>
        <Modal.Body>
          <p>{vendor.dailyPromo?.description || 'No description provided.'}</p>
        </Modal.Body>
        <Modal.Footer>
          <Button variant="secondary" onClick={() => setShowPromoModal(false)}>
            Close
          </Button>
        </Modal.Footer>
      </Modal>
    </Card>
  );
};

export default VendorDetails;
