import React, { useState, useEffect } from 'react';
import { Navbar, Nav, Image } from 'react-bootstrap';
import { Link, useNavigate } from 'react-router-dom';
import axios from 'axios';
import { GeoAlt } from 'react-bootstrap-icons';

const CustomNavbar = () => {
  const [vendor, setVendor] = useState(null); // Holds vendor data
  const [storeLogo, setStoreLogo] = useState(null);
  const [parsedAddress, setParsedAddress] = useState({ street: '', city: '', stateAbbreviation: '' }); // User's address
  const navigate = useNavigate(); // For navigation

  useEffect(() => {
    // Fetch vendor details if logged in
    const fetchVendorData = async () => {
      const token = localStorage.getItem('token'); // Get token from localStorage
      if (token) {
        try {
          const response = await axios.get('/api/vendors/profile', {
            headers: { Authorization: `Bearer ${token}` },
          });
          const vendorData = response.data.data;
          setVendor(vendorData);
          setStoreLogo(vendorData.logoUrl);
        } catch (error) {
          console.error('Error fetching vendor data:', error);
        }
      }
    };

    // Parse user's saved address
    const savedAddress = localStorage.getItem('userAddress');
    if (savedAddress) {
      const parts = savedAddress.split(',');
      setParsedAddress({
        street: parts[0]?.trim() || '',
        city: parts[1]?.trim() || '',
        stateAbbreviation: parts[2]?.trim().split(' ')[0] || '',
      });
    }

    fetchVendorData();
  }, []);

  // Function to clear address and navigate home
  const clearAddressAndNavigate = () => {
    localStorage.removeItem('userAddress');
    navigate('/');
  };

  return (
    <Navbar style={{ backgroundColor: '#FFFAFA' }} expand="lg" className="px-3 navbar">
      {/* Left Side: Logo */}
      <Navbar.Brand as={Link} to="/" className="d-flex align-items-center">
        <Image
          src={require('../assets/images/Logo.jpg')}
          alt="logo"
          height="32"
          className="d-inline-block align-top rounded me-2"
        />
      </Navbar.Brand>

      {/* Middle: User Address */}
      <div className="d-flex justify-content-center flex-grow-1">
        <div
          className="d-flex align-items-center"
          style={{
            fontSize: '13px',
            fontWeight: 'bold',
            fontFamily: 'Poppins, sans-serif',
            marginLeft: '-24px',
            cursor: 'pointer',
            color: '#333333',
          }}
          onClick={clearAddressAndNavigate} // Clears address on click
        >
          <GeoAlt className="me-2" size={15} />
          {parsedAddress.street || parsedAddress.city || parsedAddress.stateAbbreviation ? (
            <span style={{ fontWeight: '600', fontFamily: 'Poppins, sans-serif' }}>
              {parsedAddress.street || `${parsedAddress.city}, ${parsedAddress.stateAbbreviation}`}
            </span>
          ) : null}
        </div>
      </div>

      {/* Right Side: Vendor Info */}
      {vendor && (
        <Nav className="ms-auto">
          <div className="d-flex align-items-center">
            <Image
              src={storeLogo || '/default-avatar.png'} // Replace with default avatar
              alt="Store Logo"
              roundedCircle
              width="32"
              height="32"
              className="me-2"
            />
            <span style={{ color: '#333333', fontWeight: 'bold' }}>{vendor.dispensaryName}</span>
          </div>
        </Nav>
      )}
    </Navbar>
  );
};

export default CustomNavbar;
