import React, { useState, useEffect } from 'react';

const MinOrderComponent = ({ minOrder, onUpdate }) => {
  const [newMinOrder, setNewMinOrder] = useState(minOrder);

  // Update newMinOrder when the prop minOrder changes
  useEffect(() => {
    setNewMinOrder(minOrder);
  }, [minOrder]);

  const handleSubmit = (e) => {
    e.preventDefault();
    if (newMinOrder < 0) {
      alert('Minimum order cannot be less than 0');
      return;
    }
    onUpdate(newMinOrder);
  };

  return (
    <form onSubmit={handleSubmit} className="p-3 border rounded shadow-sm">
      <div className="d-flex align-items-center">
        {/* Label */}
        <label htmlFor="minOrder" className="form-label me-2 mb-0">
          Minimum Order:
        </label>

        {/* Input Group */}
        <div className="input-group" style={{ maxWidth: '150px' }}>
          <span className="input-group-text" style={{ fontWeight: 'bold' }}>
            $
          </span>
          <input
            type="number"
            id="minOrder"
            className="form-control text-end"
            value={newMinOrder}
            onChange={(e) => setNewMinOrder(e.target.value.slice(0, 3))} // Restrict to 3 digits
            min="0"
            max="999"
            style={{ paddingRight: '10px' }}
          />
        </div>

        {/* Update Button */}
        <button type="submit" className="btn btn-primary ms-3">
          Update
        </button>
      </div>
    </form>
  );
};

export default MinOrderComponent;
