import React from 'react';

const VendorSignupHighlight = () => {
  const scrollToForm = () => {
    const form = document.getElementById('registrationForm');
    if (form) {
      form.scrollIntoView({ behavior: 'smooth' });
    }
  };

  return (
    <div
      style={{
        backgroundColor: '#f9f9f6',
        border: '1px solid #ddd',
        borderRadius: '8px',
        padding: '2rem',
        marginTop: '2rem',
        textAlign: 'center',
        boxShadow: '0 4px 8px rgba(0, 0, 0, 0.1)',
      }}
    >
      <h3 style={{ color: '#333', fontWeight: 'bold' }}>Why Join WeedHurry?</h3>
      <p style={{ color: '#555', margin: '1rem 0' }}>
        Signing up as a vendor is simple, fast, and completely{' '}
        <strong style={{ color: '#28a745' }}>FREE</strong>.
      </p>
      <div
        style={{
          display: 'flex',
          flexWrap: 'wrap',
          justifyContent: 'center',
          gap: '2rem',
          marginTop: '1.5rem',
        }}
      >
        <div style={{ maxWidth: '300px', textAlign: 'center' }}>
          <img
            src="/images/easy-to-join.png"
            alt="Easy to Join"
            style={{ width: '100px', height: 'auto', marginBottom: '1rem' }}
          />
          <h5 style={{ color: '#333', fontWeight: 'bold' }}>Effortless Onboarding</h5>
          <p style={{ color: '#555', fontSize: '0.9rem' }}>
            Get started in minutes. No lengthy processes or hidden charges.
          </p>
        </div>
        <div style={{ maxWidth: '300px', textAlign: 'center' }}>
          <img
            src="/images/delivery-zone.png"
            alt="Delivery Zone"
            style={{ width: '100px', height: 'auto', marginBottom: '1rem' }}
          />
          <h5 style={{ color: '#333', fontWeight: 'bold' }}>Custom Delivery Zones</h5>
          <p style={{ color: '#555', fontSize: '0.9rem' }}>
            Define your own delivery area to reach customers nearby.
          </p>
        </div>
        <div style={{ maxWidth: '300px', textAlign: 'center' }}>
          <img
            src="/images/track-visits.png"
            alt="Track Visits"
            style={{ width: '100px', height: 'auto', marginBottom: '1rem' }}
          />
          <h5 style={{ color: '#333', fontWeight: 'bold' }}>Track Your Success</h5>
          <p style={{ color: '#555', fontSize: '0.9rem' }}>
            Monitor visits to your page and grow your business with real insights.
          </p>
        </div>
      </div>
      <div style={{ marginTop: '2rem' }}>
        <button
          style={{
            backgroundColor: '#28a745',
            color: 'white',
            border: 'none',
            padding: '0.75rem 1.5rem',
            fontSize: '1rem',
            borderRadius: '8px',
            cursor: 'pointer',
          }}
          onClick={scrollToForm}
        >
          Start Now for Free
        </button>
      </div>
    </div>
  );
};

export default VendorSignupHighlight;
